/*
 * Helper script for diamond upgrade (only viewer facets)
 *
 * Usage:
 * 1. Check that the old deployment json is present (deployments/<env>.json) and not rewritten with something new
 * 2. Run the script with the following command:
 *    CONTRACTS_ENV_NAME=stage forge script UpdateDiamondViewMultisig --rpc-url https://rpc.stage.fluence.dev -vvv --skip-simulation --private-key ... --broadcast
 *    The script will deploy new facets and DealImpl, and generate a multicall transaction to update the diamond.
 * 3. Check the output of the script, especially the generated transaction.
 * 4. The script also upgrades Diamond on fork (not really) to check if the transaction is valid.
 *    It checks difficulty before and after upgrade and logs it in console.
 * 5. If everything is fine, run the transaction in the multisig UI.
 */

pragma solidity ^0.8.19;

import {Script, console2} from "forge-std/Script.sol";

import {Deployment} from "script/utils/Deployment.sol";
import {SelectorsHelper} from "script/utils/SelectorsHelper.sol";
import {IDiamondCut} from "src/interfaces/IDiamondCut.sol";
import {IDiamondLoupe} from "src/interfaces/IDiamondLoupe.sol";
import {ICapacityConst} from "src/core/interfaces/ICapacityConst.sol";
import {IViewer} from "src/interfaces/IViewer.sol";


contract UpdateDiamondViewsMultisig is Script, Deployment, SelectorsHelper {
    string constant DEPLOYMENTS_PATH = "/deployments/";
    string private fullDeploymentsPath;
    string envName;

    address constant REAL_MULTISIG_ON_ALL_ENVS = 0xEaf9135D113b720259d923c044d621e604E1B533;

    function setUp() external {
        envName = vm.envString("CONTRACTS_ENV_NAME");
        string memory fileNames = string.concat(envName, ".json");
        fullDeploymentsPath = string.concat(vm.projectRoot(), DEPLOYMENTS_PATH, fileNames);
    }

    function contractAddress(string memory env, string memory name) private view returns (address) {
        string memory path = string.concat(vm.projectRoot(), "/deployments/", env, ".json");
        console2.log("path: %s", path);
        string memory json = vm.readFile(path);
        bytes memory data = vm.parseJson(json, string.concat(".", name, ".addr"));
        address c = abi.decode(data, (address));
        console2.log("contract %s@%s is: %s", name, env, c);
        return c;
    }

    function makeDiamondCut() private returns (bytes memory) {
        address diamond = contractAddress(envName, "Diamond");

        address viewerFacet = contractAddress(envName, "ViewerFacet");

        // EMERGENCY BLOCK (use if we don't have the actual addresses in the deployment json)
        // address viewerFacet = IDiamondLoupe(diamond).facetAddress(IViewer.getCommitmentStatuses.selector);
        // END OF EMERGENCY BLOCK

        // let's check validity of facet address in deployment json
        IDiamondLoupe.Facet[] memory actualFacets = IDiamondLoupe(diamond).facets();
        bool found = false;
        for (uint256 j = 0; j < actualFacets.length; j++) {
            if (actualFacets[j].facetAddress == viewerFacet) {
                found = true;
                break;
            }
        }
        require(found, "UpdateDiamondViewsMultisig: viewer facet not matched, probably you have unwanted local changes");

        // first part of diamondCut is for removing all selectors from facets
        // second part is for adding new facets
        // so we need to double the length of facets
        IDiamondCut.FacetCut[] memory diamondCut = new IDiamondCut.FacetCut[](2);
        bytes4[] memory facetFunctionSelectors = IDiamondLoupe(diamond).facetFunctionSelectors(viewerFacet);
        diamondCut[0] = IDiamondCut.FacetCut({
            facetAddress: address(0),
            action: IDiamondCut.FacetCutAction.Remove,
            functionSelectors: facetFunctionSelectors
        });

        // deploy new facets
        address newViewerFacet = _deployContract("ViewerFacet", "ViewerFacet", new bytes(0));

        diamondCut[1] = IDiamondCut.FacetCut({
            facetAddress: newViewerFacet,
            action: IDiamondCut.FacetCutAction.Add,
            functionSelectors: getViewerSelectors()
        });
        bytes memory _calldata = abi.encodeWithSelector(IDiamondCut.diamondCut.selector, diamondCut, address(0), "");

        return _calldata;
    }

    function _startDeploy() internal virtual {
        _loadDeployment(fullDeploymentsPath);

        console2.log("\nStart deploying...");
    }

    function _stopDeploy() internal virtual {
        _printDeployments();
        _saveDeployment(fullDeploymentsPath);

        console2.log("\nDeploy finished");
    }


    function run() external {
        vm.startBroadcast();
        _startDeploy();

        address diamond = contractAddress(envName, "Diamond");

        bytes memory _tx = makeDiamondCut();

        _stopDeploy();
        vm.stopBroadcast(); // we have stopped the broadcast, so we don't send any transactions below (do it on fork)

        console2.log("\n\n\nUpgrade bytecode:\n");
        console2.logBytes(_tx); // log the transaction
        console2.log("\n\n\n=================\n");

        // check if the transaction is valid and difficulty isn't changed
        bytes32 difficultyBefore = ICapacityConst(diamond).difficulty();

        vm.startPrank(REAL_MULTISIG_ON_ALL_ENVS);
        (bool success, ) = diamond.call(_tx);
        require(success, "UpdateDiamondMultisig: failed to update diamond");

        bytes32 difficultyAfter = ICapacityConst(diamond).difficulty();
        require(difficultyBefore == difficultyAfter, "UpdateDiamondMultisig: difficulty changed");
        console2.log("Difficulty not changed:");
        console2.logBytes32(ICapacityConst(diamond).difficulty());
    }
}

