pragma solidity ^0.8.19;

import {IBaseOffer} from "src/core/interfaces/IBaseOffer.sol";
import {ICore} from "src/core/interfaces/ICore.sol";
import {ICapacityConst} from "src/core/interfaces/ICapacityConst.sol";
import {IEpochController} from "src/core/interfaces/IEpochController.sol";
import {IGlobalConst} from "src/core/interfaces/IGlobalConst.sol";
import {IMarket} from "src/core/interfaces/IMarket.sol";
import {IMarketV2} from "src/core/interfaces/IMarketV2.sol";
import {IOffer} from "src/core/interfaces/IOffer.sol";
import {IOfferV2} from "src/core/interfaces/IOfferV2.sol";
import {IDealFactory} from "src/core/interfaces/IDealFactory.sol";
import {IDiamondCut} from "src/interfaces/IDiamondCut.sol";
import {IDiamondLoupe} from "src/interfaces/IDiamondLoupe.sol";
import {IMulticall} from "src/interfaces/IMulticall.sol";
import {ICapacity} from "src/core/interfaces/ICapacity.sol";
import {IViewer} from "src/interfaces/IViewer.sol";
import {IERC165} from "src/interfaces/IERC165.sol";
import {IERC173} from "src/interfaces/IERC173.sol";
import {IWhitelist} from "src/interfaces/IWhitelist.sol";

abstract contract SelectorsHelper {
    function getDiamondCut(
        address coreFacet,
        address offerFacet,
        address marketFacet,
        address offerV2Facet,
        address marketV2Facet,
        address capacityFacet,
        address dealFactoryFacet,
        address diamondLoupeFacet,
        address ownershipFacet,
        address multicallFacet,
        address viewerFacet
    ) internal pure returns (IDiamondCut.FacetCut[] memory) {
        uint256 COUNT = 11;
        uint256 i = 0;
        IDiamondCut.FacetCut[] memory diamondCut = new IDiamondCut.FacetCut[](COUNT);

        diamondCut[i++] = IDiamondCut.FacetCut({
            facetAddress: coreFacet,
            action: IDiamondCut.FacetCutAction.Add,
            functionSelectors: getCoreSelectors()
        });
        diamondCut[i++] = IDiamondCut.FacetCut({
            facetAddress: offerFacet,
            action: IDiamondCut.FacetCutAction.Add,
            functionSelectors: getOfferSelectors()
        });
        diamondCut[i++] = IDiamondCut.FacetCut({
            facetAddress: marketFacet,
            action: IDiamondCut.FacetCutAction.Add,
            functionSelectors: getMarketSelectors()
        });
        diamondCut[i++] = IDiamondCut.FacetCut({
            facetAddress: offerV2Facet,
            action: IDiamondCut.FacetCutAction.Add,
            functionSelectors: getOfferV2Selectors()
        });
        diamondCut[i++] = IDiamondCut.FacetCut({
            facetAddress: marketV2Facet,
            action: IDiamondCut.FacetCutAction.Add,
            functionSelectors: getMarketV2Selectors()
        });
        diamondCut[i++] = IDiamondCut.FacetCut({
            facetAddress: capacityFacet,
            action: IDiamondCut.FacetCutAction.Add,
            functionSelectors: getCapacitySelectors()
        });
        diamondCut[i++] = IDiamondCut.FacetCut({
            facetAddress: dealFactoryFacet,
            action: IDiamondCut.FacetCutAction.Add,
            functionSelectors: getDealFactorySelectors()
        });
        diamondCut[i++] = IDiamondCut.FacetCut({
            facetAddress: diamondLoupeFacet,
            action: IDiamondCut.FacetCutAction.Add,
            functionSelectors: getDiamondLoupeSelectors()
        });
        diamondCut[i++] = IDiamondCut.FacetCut({
            facetAddress: ownershipFacet,
            action: IDiamondCut.FacetCutAction.Add,
            functionSelectors: getOwnershipSelectors()
        });
        diamondCut[i++] = IDiamondCut.FacetCut({
            facetAddress: multicallFacet,
            action: IDiamondCut.FacetCutAction.Add,
            functionSelectors: getMulticallSelectors()
        });
        diamondCut[i++] = IDiamondCut.FacetCut({
            facetAddress: viewerFacet,
            action: IDiamondCut.FacetCutAction.Add,
            functionSelectors: getViewerSelectors()
        });

        assert(i == COUNT);
        return diamondCut;
    }

    function getOwnershipSelectors() internal pure returns (bytes4[] memory) {
        uint256 COUNT = 2;
        uint256 i = 0;

        bytes4[] memory selectors = new bytes4[](COUNT);
        selectors[i++] = IERC173.transferOwnership.selector;
        selectors[i++] = IERC173.owner.selector;

        assert(i == COUNT);
        return selectors;
    }

    function getCoreSelectors() internal pure returns (bytes4[] memory) {
        uint256 COUNT = 50;
        uint256 i = 0;

        bytes4[] memory selectors = new bytes4[](COUNT);
        selectors[i++] = ICore.dealImpl.selector;
        selectors[i++] = ICore.dealV2Impl.selector;
        selectors[i++] = ICore.setDealImpl.selector;
        selectors[i++] = ICore.setDealV2Impl.selector;
        selectors[i++] = IGlobalConst.precision.selector;
        selectors[i++] = IGlobalConst.minDealDepositedEpochs.selector;
        selectors[i++] = IGlobalConst.minDealRematchingEpochs.selector;
        selectors[i++] = IGlobalConst.minProtocolVersion.selector;
        selectors[i++] = IGlobalConst.maxProtocolVersion.selector;
        selectors[i++] = IGlobalConst.maxUnitCountPerProvider.selector;
        selectors[i++] = IGlobalConst.setConstant.selector;
        selectors[i++] = IGlobalConst.addPaymentTokenToWhitelist.selector;
        selectors[i++] = IGlobalConst.removePaymentTokenFromWhitelist.selector;
        selectors[i++] = IGlobalConst.isPaymentTokenAllowed.selector;
        selectors[i++] = IGlobalConst.getAllConstants.selector;
        selectors[i++] = ICapacityConst.fltPrice.selector;
        selectors[i++] = ICapacityConst.fltPriceAtEpoch.selector;
        selectors[i++] = ICapacityConst.fltCollateralPerUnit.selector;
        selectors[i++] = ICapacityConst.usdCollateralPerUnit.selector;
        selectors[i++] = ICapacityConst.usdTargetRevenuePerEpoch.selector;
        selectors[i++] = ICapacityConst.minDuration.selector;
        selectors[i++] = ICapacityConst.maxDuration.selector;
        selectors[i++] = ICapacityConst.minCUPerCommitment.selector;
        selectors[i++] = ICapacityConst.maxCUPerCommitment.selector;
        selectors[i++] = ICapacityConst.minRewardPerEpoch.selector;
        selectors[i++] = ICapacityConst.maxRewardPerEpoch.selector;
        selectors[i++] = ICapacityConst.vestingPeriodDuration.selector;
        selectors[i++] = ICapacityConst.vestingPeriodCount.selector;
        selectors[i++] = ICapacityConst.slashingRate.selector;
        selectors[i++] = ICapacityConst.minProofsPerEpoch.selector;
        selectors[i++] = ICapacityConst.maxProofsPerEpoch.selector;
        selectors[i++] = ICapacityConst.withdrawEpochsAfterFailed.selector;
        selectors[i++] = ICapacityConst.maxFailedRatio.selector;
        selectors[i++] = ICapacityConst.activeUnitCount.selector;
        selectors[i++] = ICapacityConst.difficulty.selector;
        selectors[i++] = ICapacityConst.randomXProxy.selector;
        selectors[i++] = ICapacityConst.oracle.selector;
        selectors[i++] = ICapacityConst.getRewardPool.selector;
        selectors[i++] = ICapacityConst.setFLTPrice.selector;
        selectors[i++] = ICapacityConst.setDifficulty.selector;
        selectors[i++] = ICapacityConst.setCapacityConstant.selector;
        selectors[i++] = ICapacityConst.setOracle.selector;
        selectors[i++] = ICapacityConst.lastPriceSetting.selector;
        selectors[i++] = IEpochController.currentEpoch.selector;
        selectors[i++] = IEpochController.epochDuration.selector;
        selectors[i++] = IEpochController.initTimestamp.selector;
        selectors[i++] = IEpochController.epochTimestamp.selector;
        selectors[i++] = IWhitelist.isApproved.selector;
        selectors[i++] = IWhitelist.grantAccess.selector;
        selectors[i++] = IWhitelist.revokeAccess.selector;

        assert(i == COUNT);
        return selectors;
    }

    function getOfferSelectors() internal pure returns (bytes4[] memory) {
        uint256 COUNT = 21;
        uint256 i = 0;

        bytes4[] memory selectors = new bytes4[](COUNT);

        selectors[i++] = IBaseOffer.getProviderInfo.selector;
        selectors[i++] = IBaseOffer.getBaseOffer.selector;
        selectors[i++] = IBaseOffer.getComputePeer.selector;
        selectors[i++] = IBaseOffer.getComputeUnit.selector;
        selectors[i++] = IBaseOffer.getComputeUnitIds.selector;
        selectors[i++] = IBaseOffer.getComputeUnits.selector;
        selectors[i++] = IBaseOffer.setProviderInfo.selector;
        selectors[i++] = IBaseOffer.changePaymentToken.selector;

        selectors[i++] = IOffer.getOffer.selector;
        selectors[i++] = IOffer.getEffectorInfo.selector;
        selectors[i++] = IOffer.registerMarketOffer.selector;
        selectors[i++] = IOffer.addComputePeers.selector;
        selectors[i++] = IOffer.addComputeUnits.selector;
        selectors[i++] = IOffer.removeOffer.selector;
        selectors[i++] = IOffer.removeComputePeer.selector;
        selectors[i++] = IOffer.removeComputeUnit.selector;
        selectors[i++] = IOffer.changeMinPricePerCuPerEpoch.selector;
        selectors[i++] = IOffer.addEffector.selector;
        selectors[i++] = IOffer.removeEffector.selector;
        selectors[i++] = IOffer.setEffectorInfo.selector;
        selectors[i++] = IOffer.removeEffectorInfo.selector;

        assert(i == COUNT);
        return selectors;
    }

    function getMarketSelectors() internal pure returns (bytes4[] memory) {
        uint256 COUNT = 3;
        uint256 i = 0;

        bytes4[] memory selectors = new bytes4[](COUNT);
        selectors[i++] = IMarket.matchDeal.selector;
        selectors[i++] = IMarket.moveResourcesFromDeal.selector;
        selectors[i++] = IMarket.reportWorkerActivated.selector;

        assert(i == COUNT);
        return selectors;
    }

    function getOfferV2Selectors() internal pure returns (bytes4[] memory) {
        uint256 COUNT = 23;
        uint256 i = 0;

        bytes4[] memory selectors = new bytes4[](COUNT);

        selectors[i++] = IOfferV2.registerMarketOfferV2.selector;
        selectors[i++] = IOfferV2.upgradeMarketOfferV2.selector;
        selectors[i++] = IOfferV2.addComputePeersV2.selector;
        selectors[i++] = IOfferV2.addComputeUnitsV2.selector;
        selectors[i++] = IOfferV2.removeOfferV2.selector;
        selectors[i++] = IOfferV2.removeComputePeerV2.selector;
        selectors[i++] = IOfferV2.removeComputeUnitV2.selector;
        selectors[i++] = IOfferV2.changeResourcePriceV2.selector;
        selectors[i++] = IOfferV2.changeResourceMaxSupplyV2.selector;
        selectors[i++] = IOfferV2.removePeerResource.selector;
        selectors[i++] = IOfferV2.registerPeerResource.selector;
        selectors[i++] = IOfferV2.registerResources.selector;
        selectors[i++] = IOfferV2.updateResourceMetadata.selector;
        selectors[i++] = IOfferV2.ramPerVcpu.selector;
        selectors[i++] = IOfferV2.vcpuPerCu.selector;
        selectors[i++] = IOfferV2.bytesPerRam.selector;
        selectors[i++] = IOfferV2.bytesPerStorage.selector;
        selectors[i++] = IOfferV2.changeResourceDetails.selector;
        selectors[i++] = IOfferV2.createDatacenter.selector;
        selectors[i++] = IOfferV2.createDatacenters.selector;
        selectors[i++] = IOfferV2.updateDatacenter.selector;
        selectors[i++] = IOfferV2.setOfferDatacenter.selector;
        selectors[i++] = IOfferV2.getOfferDatacenter.selector;

        assert(i == COUNT);
        return selectors;
    }

    function getMarketV2Selectors() internal pure returns (bytes4[] memory) {
        uint256 COUNT = 5;
        uint256 i = 0;

        bytes4[] memory selectors = new bytes4[](COUNT);

        selectors[i++] = IMarketV2.matchDealV2.selector;
        selectors[i++] = IMarketV2.reportWorkerActivatedV2.selector;
        selectors[i++] = IMarketV2.reportAppCIDUpdatedV2.selector;
        selectors[i++] = IMarketV2.moveResourcesFromDealV2.selector;
        selectors[i++] = IMarketV2.getWorkerData.selector;

        assert(i == COUNT);
        return selectors;
    }

    function getCapacitySelectors() internal pure returns (bytes4[] memory) {
        uint256 COUNT = 20;
        uint256 i = 0;

        bytes4[] memory selectors = new bytes4[](COUNT);
        selectors[i++] = ICapacity.getStatus.selector;
        selectors[i++] = ICapacity.getCommitment.selector;
        selectors[i++] = ICapacity.totalRewards.selector;
        selectors[i++] = ICapacity.unlockedRewards.selector;
        selectors[i++] = ICapacity.withdrawnRewards.selector;
        selectors[i++] = ICapacity.getGlobalNonce.selector;
        selectors[i++] = ICapacity.getRewardInfo.selector;
        selectors[i++] = ICapacity.getRewardBalance.selector;
        selectors[i++] = ICapacity.createCommitment.selector;
        selectors[i++] = ICapacity.removeCommitment.selector;
        selectors[i++] = ICapacity.finishCommitment.selector;
        selectors[i++] = ICapacity.depositCollateral.selector;
        selectors[i++] = ICapacity.submitProofs.selector;
        selectors[i++] = ICapacity.submitProof.selector;
        selectors[i++] = ICapacity.removeCUFromCC.selector;
        selectors[i++] = ICapacity.withdrawReward.selector;
        selectors[i++] = ICapacity.diamondReceive.selector;
        selectors[i++] = ICapacity.withdrawCollateral.selector;
        selectors[i++] = ICapacity.getProofCounts.selector;
        selectors[i++] = ICapacity.getUnitExitStatuses.selector;

        assert(i == COUNT);
        return selectors;
    }

    function getDealFactorySelectors() internal pure returns (bytes4[] memory) {
        uint256 COUNT = 13;
        uint256 i = 0;

        bytes4[] memory selectors = new bytes4[](COUNT);
        selectors[i++] = IDealFactory.hasDeal.selector;
        selectors[i++] = IDealFactory.deployDeal.selector;
        selectors[i++] = IDealFactory.deployDealV2.selector;
        selectors[i++] = IDealFactory.fundDeal.selector;
        selectors[i++] = IDealFactory.fundDealV2.selector;
        selectors[i++] = IDealFactory.setBalanceKeeper.selector;
        selectors[i++] = IDealFactory.balanceKeeper.selector;
        selectors[i++] = IDealFactory.setVodopad.selector;
        selectors[i++] = IDealFactory.vodopad.selector;
        selectors[i++] = IDealFactory.stopDealHandler.selector;
        selectors[i++] = IDealFactory.stopDealHandlerV2.selector;
        selectors[i++] = IDealFactory.storedDealCount.selector;
        selectors[i++] = IDealFactory.storedDeals.selector;

        assert(i == COUNT);
        return selectors;
    }

    function getDiamondLoupeSelectors() internal pure returns (bytes4[] memory) {
        bytes4[] memory selectors = new bytes4[](5);
        selectors[0] = IDiamondLoupe.facets.selector;
        selectors[1] = IDiamondLoupe.facetFunctionSelectors.selector;
        selectors[2] = IDiamondLoupe.facetAddresses.selector;
        selectors[3] = IDiamondLoupe.facetAddress.selector;
        selectors[4] = IERC165.supportsInterface.selector;
        return selectors;
    }

    function getDiamondCutSelectors() internal pure returns (bytes4[] memory) {
        bytes4[] memory selectors = new bytes4[](1);
        selectors[0] = IDiamondCut.diamondCut.selector;
        return selectors;
    }

    function getMulticallSelectors() internal pure returns (bytes4[] memory) {
        bytes4[] memory selectors = new bytes4[](1);
        selectors[0] = IMulticall.multicall.selector;
        return selectors;
    }

    function getViewerSelectors() internal pure returns (bytes4[] memory) {
        uint256 COUNT = 9;
        uint256 i = 0;

        bytes4[] memory selectors = new bytes4[](COUNT);
        selectors[i++] = IViewer.getCommitmentStatuses.selector;
        selectors[i++] = IViewer.getDatacenter.selector;
        selectors[i++] = IViewer.getDatacenters.selector;
        selectors[i++] = IViewer.getResources.selector;
        selectors[i++] = IViewer.getResource.selector;
        selectors[i++] = IViewer.getOffersV2.selector;
        selectors[i++] = IViewer.getOfferV2.selector;
        selectors[i++] = IViewer.getPeersV2.selector;
        selectors[i++] = IViewer.getPeerV2.selector;

        assert(i == COUNT);
        return selectors;
    }
}

