pragma solidity ^0.8.0;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IBalanceKeeper {
    enum BalanceChangeReason {
        USER_TOP_UP,
        MOVE_FROM_DEAL,
        MOVE_FREE_BALANCE,
        MOVE_TO_DEAL,
        WITHDRAW_BY_DEAL,
        TERMINATE,
        SEND_TO_LIMBO,
        FUND_FROM_LIMBO,
        REFUND
    }

    enum DealDepositReason {
        FUND_DEAL,
        DEPLOY_DEAL
    }

    struct DealDepositInfo {
        IERC20 token;
        address deal;
        address user;
        DealDepositReason reason;
        uint256 amount;
        uint256 blockNumber;
        uint256 reserved1; // to extend the struct as elements are stacked in array
        uint256 reserved2; // to extend the struct as elements are stacked in array
        uint256 reserved3; // to extend the struct as elements are stacked in array
    }

    event FreeBalanceIncrease(IERC20 indexed token, address indexed user, BalanceChangeReason indexed reason, uint256 amount, address info);
    event FreeBalanceDecrease(IERC20 indexed token, address indexed user, BalanceChangeReason indexed reason, uint256 amount, address info);
    event BalanceMoved(IERC20 indexed token, address indexed from, address indexed to, uint256 amount, uint8 reserved);
    event DealDeposit(
        IERC20 indexed token,
        address indexed deal,
        address indexed user,
        uint256 amount,
        DealDepositReason reason,
        uint256 blockNumber,
        uint256 depositIndex // index in array
    );
    event DealWithdraw(IERC20 indexed token, address indexed deal, address indexed user, uint256 amount, uint8 reserved);
    event CoordinatorSet(address indexed coordinator, address initiator);
    event PoolTopUp(IERC20 indexed token, address indexed initiator, uint256 amount);
    event PoolWithdraw(IERC20 indexed token, address indexed initiator, uint256 amount);
    event UserBalanceIncrease(IERC20 indexed token, address indexed user, address indexed initiator, uint256 amount, string topUpId);
    event DealMigrated(address indexed deal, IERC20 indexed token, uint256 amount);
    event ManualWithdrawEnabled(address initiator);
    event ManualWithdrawDisabled(address initiator);
    event Refunded(IERC20 indexed token, address indexed user, uint256 amount, address initiator);
    event SentToLimbo(IERC20 indexed token, address indexed user, address initiator, uint256 amount);
    event AccountFreezeStateChanged(address indexed user, bool frozen, address initiator);
    event Withdrawn(IERC20 indexed token, address indexed user, uint256 amount, uint8 reserved, address initiator);
    event TerminationVeto(IERC20 indexed token, address indexed user, address initiator);
    event TerminationRequestCreated(IERC20 indexed token, address indexed user, address initiator);
    event TerminationExecuted(IERC20 indexed token, address indexed user, address initiator);
    event WithdrawnFromLimbo(IERC20 indexed token, address indexed user, address indexed to, address initiator, uint256 amount);
    event FundedFromLimbo(IERC20 indexed token, address indexed user, address initiator, uint256 amount);

    // STATE
    function diamond() external view returns (address);
    function coordinator() external view returns (address);
    function manualWithdrawEnabled() external view returns (bool);
    function WITHDRAW_REQUEST_DELAY() external view returns (uint256);
    function freeBalanceTotal(IERC20 token) external view returns (uint256);
    function dealBalancesTotal(IERC20 token) external view returns (uint256);
    function poolBalance(IERC20 token) external view returns (uint256);
    function limbo(IERC20 token, address user) external view returns (uint256);
    function limboTotal(IERC20 token) external view returns (uint256);
    function frozen(address user) external view returns (bool);
    function topUpIds(string calldata) external view returns (bool);

    // INITIALIZATION
    function initialize(address _diamond) external;

    // VIEW METHODS
    function getFreeBalance(IERC20 token, address user) external view returns (uint256);
    function getFullBalance(IERC20 token, address user) external view returns (uint256);
    function getDepositInfoLength() external view returns (uint256);
    function getDepositInfo(uint256 from, uint256 to) external view returns (DealDepositInfo[] memory);

    // COORDINATOR
    function poolTopUp(IERC20 token, uint256 amount) external;
    function poolWithdraw(IERC20 token, uint256 amount) external;
    function enableOnchainRefund() external;
    function disableOnchainRefund() external;
    function refund(IERC20 token, address user, uint256 amount) external;
    function freezeAccount(address user) external;
    function unfreezeAccount(address user) external;
    function sendToLimbo(IERC20 token, address user) external;
    function terminationVeto(IERC20 token, address user) external;

    // VODOPAD
    function userTopUp(IERC20 token, address user, uint256 amount, string memory topUpId) external;

    // DEAL
    function moveBalanceFromDeal(IERC20 token, address user, uint256 amount, uint8 reserved) external;
    function moveBalanceToDeal(IERC20 token, address user, uint256 amount, DealDepositReason reason) external;
    function moveFreeBalance(IERC20 token, address from, address to, uint256 amount, uint8 reserved) external;
    function withdrawByDeal(IERC20 token, address user, uint256 amount, uint8 reserved) external;

    // USER
    function terminationRequest(IERC20 token) external;
    function terminate(IERC20 token) external;

    // ADMIN
    function setCoordinator(address _coordinator) external;
    function withdrawFromLimbo(IERC20 token, address user, address to, uint256 amount) external;
    function fundFromLimbo(IERC20 token, address user, uint256 amount) external;
}

