pragma solidity ^0.8.19;

import {IDeal} from "src/deal/v1/interfaces/IDeal.sol";
import {IDealV2} from "src/deal/interfaces/IDeal.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {CIDV1} from "src/utils/Common.sol";
import {IConfig} from "src/deal/v1/interfaces/IConfig.sol";
import {IConfigV2} from "src/deal/interfaces/IConfig.sol";
import {IOfferV2} from "src/core/interfaces/IOfferV2.sol";
import {IBalanceKeeper} from "src/balance-keeper/IBalanceKeeper.sol";

/// @title Deal factory contract interface
/// @dev Deal factory contract is responsible for creating deals
interface IDealFactory {
    // ----------------- Events -----------------
    /// @dev Emitted when a new deal is created
    /// @param owner The owner of the deal
    /// @param deal The deal address
    /// @param createdAtEpoch The creation time of the deal in Epochs
    /// @param paymentToken The payment token of the deal. User can choose any ERC20 token for payment
    /// @param minWorkers The min workers of the deal. Deel will be activated only if the number of workers is greater
    /// @param targetWorkers The target workers of the deal. It's the max number of workers for the deal
    /// @param cuCountPerWorker CU count per worker
    /// @param maxWorkersPerProvider The max workers per provider of the deal
    /// @param pricePerCuPerEpoch The price per CU per epoch of the deal
    /// @param effectors The effectors of the deal
    /// @param appCID The app cid of the deal
    /// @param providersAccessType_ The providers access type of the deal (None, Whitelist, Blacklist)
    /// @param providersAccessList_ The providers access list of the deal
    /// @param protocolVersion_ Protocol version
    event DealCreated(
        address indexed owner,
        IDeal deal,
        uint256 createdAtEpoch,
        IERC20 paymentToken,
        uint256 minWorkers,
        uint256 targetWorkers,
        uint256 cuCountPerWorker,
        uint256 maxWorkersPerProvider,
        uint256 pricePerCuPerEpoch,
        CIDV1[] effectors,
        CIDV1 appCID,
        IConfig.AccessType providersAccessType_,
        address[] providersAccessList_,
        uint256 protocolVersion_
    );

    event DealCreatedV2(
        address indexed owner,
        IDealV2 deal,
        uint256 createdAtEpoch,
        IERC20 paymentToken,
        uint256 targetWorkers,
        IOfferV2.ResourceRequirement[] resources,
        uint256 pricePerEpoch,
        uint256 maxWorkersPerProvider,
        CIDV1 appCID,
        IConfigV2.AccessTypeV2 providersAccessType_,
        address[] providersAccessList_,
        uint256 protocolVersion_
    );

    event DealStopped(address indexed owner, address indexed deal, uint256 endedEpoch);
    event DealStoppedV2(address indexed owner, address indexed deal, uint256 endedEpoch);

    event DealFunded(address indexed owner, address indexed deal, uint256 amount, uint256 newMaxPaidEpoch);
    event DealFundedV2(address indexed owner, address indexed deal, uint256 amount, uint256 newMaxPaidEpoch);

    event VodopadSet(address indexed vodopad, address initiator);

    // ----------------- View -----------------
    /// @dev Returns true if the deal exists
    /// @param deal The deal address
    function hasDeal(address deal) external view returns (bool);

    // ----------------- Mutable -----------------
    /// @dev Creates a new deal
    /// @param appCID_ The app cid of the deal
    /// @param paymentToken_ The payment token of the deal. User can choose any ERC20 token for payment
    /// @param depositAmount_ The deposit amount of the deal
    /// @param minWorkers_ The min workers of the deal. Deel will be activated only if the number of workers is greater
    /// @param targetWorkers_ The target workers of the deal. It's the max number of workers for the deal
    /// @param maxWorkersPerProvider_ The max workers per provider of the deal
    /// @param pricePerCuPerEpoch_ The price per CU per epoch of the deal
    /// @param effectors_ The effectors of the deal
    /// @param providersAccessType_ The providers access type of the deal (None, Whitelist, Blacklist)
    /// @param providersAccessList_ The providers access list of the deal
    /// @param protocolVersion_ Protocol version
    /// @return The deal address
    function deployDeal(
        CIDV1 calldata appCID_,
        IERC20 paymentToken_,
        uint256 depositAmount_,
        uint256 minWorkers_,
        uint256 targetWorkers_,
        uint256 cuCountPerWorker_,
        uint256 maxWorkersPerProvider_,
        uint256 pricePerCuPerEpoch_,
        CIDV1[] calldata effectors_,
        IConfig.AccessType providersAccessType_,
        address[] calldata providersAccessList_,
        uint256 protocolVersion_,
        address owner_
    ) external returns (IDeal);

    /// @dev Creates a new deal v2
    function deployDealV2(
        CIDV1 calldata appCID_,
        IERC20 paymentToken_,
        uint256 depositAmount_,
        uint256 targetWorkers_,
        IOfferV2.ResourceRequirement[] calldata resources_,
        uint256 pricePerEpoch_,
        uint256 maxWorkersPerProvider_,
        IConfigV2.AccessTypeV2 providersAccessType_,
        address[] calldata providersAccessList_,
        uint256 protocolVersion_,
        address owner_
    ) external returns (IDealV2);

    // ----------------- Mutable -----------------
    // TODO move to market contract
    function fundDeal(IDeal deal, uint256 targetMaxPaidEpoch) external;
    function fundDealV2(IDealV2 deal, uint256 targetMaxPaidEpoch) external;
    function setBalanceKeeper(IBalanceKeeper balanceKeeper) external;
    function balanceKeeper() external view returns (IBalanceKeeper);
    function setVodopad(address vodopad) external;
    function vodopad() external view returns (address);
    function stopDealHandler() external;
    function stopDealHandlerV2() external;
    function storedDealCount() external returns (uint256);
    function storedDeals(uint256 from, uint256 to) external returns (address[] memory);
}

