pragma solidity ^0.8.19;


import {ICapacityConst} from "src/core/interfaces/ICapacityConst.sol";

/// @title Global Constants contract interface
/// @dev Global Constants contract stores global constants of the system and it is responsible for changing them
interface IGlobalConst is ICapacityConst {
    // ------------------ Events ------------------
    /// @dev Emitted when a constant with uint256 value is updated
    /// @param constantType Constant type
    /// @param newValue New uint256 value
    event ConstantUpdated(ConstantType constantType, uint256 newValue);

    event PaymentTokenAddedToWhitelist(address indexed paymentToken);

    event PaymentTokenRemovedFromWhitelist(address indexed paymentToken);

    // ------------------ Types ------------------
    enum ConstantType {
        MinDealDepositedEpochs,
        MinDealRematchingEpochs,
        MinProtocolVersion,
        MaxProtocolVersion,
        MaxUnitCountPerProvider
    }

    struct Constants1 {
        uint256 fltPrice;
        uint256 fltCollateralPerUnit;
        uint256 usdCollateralPerUnit;
        uint256 usdTargetRevenuePerEpoch;
        uint256 minDuration;
        uint256 maxDuration;
        uint256 minRewardPerEpoch;
        uint256 maxRewardPerEpoch;
        uint256 vestingPeriodDuration;
        uint256 vestingPeriodCount;
        uint256 slashingRate;
        uint256 minProofsPerEpoch;
        uint256 maxProofsPerEpoch;
        uint256 withdrawEpochsAfterFailed;
        uint256 maxFailedRatio;
    }

    struct Constants2 {
        uint256 epochDuration;
        uint256 minDealRematchingEpoch;
        uint256 minDealDepositedEpoch;
        uint256 minCUPerCommitment;
        uint256 maxCUPerCommitment;
        uint256 precision;
        uint256 initTimestamp;
        bytes32 difficulty;
    }

    struct GlobalConstants {
        Constants1 constants1;
        Constants2 constants2;
    }

    // ------------------ External Constants ------------------
    /// @dev Returns precision for decimal values (USD, percentage)
    function precision() external view returns (uint256);

    // ------------------ External View Functions ------------------

    /// @dev Returns min deposited Epochs constant for new deals
    /// @return min deposited Epochs for new deals
    function minDealDepositedEpochs() external view returns (uint256);

    /// @dev Returns min rematching Epochs constant for all deals
    /// @return min rematching Epochs for all deals
    function minDealRematchingEpochs() external view returns (uint256);

    /// @dev Returns min protocol version which can be specified in offers and deals
    /// @return min protocol version
    function minProtocolVersion() external view returns (uint256);

    /// @dev Returns max protocol version which can be specified in offers and deals
    /// @return max protocol version
    function maxProtocolVersion() external view returns (uint256);

    /// @dev Returns max unit count allowed for single provider to have in all offers combined
    /// @return max unit count per provider
    function maxUnitCountPerProvider() external view returns (uint256);

    /// @dev Adds payment token address to whitelist
    function addPaymentTokenToWhitelist(address paymentToken) external;

    /// @dev Removes payment token address from whitelist
    function removePaymentTokenFromWhitelist(address paymentToken) external;

    /// @dev Checks if address is allowed to use as payment token in deals/offers
    /// @return true if address is allowed
    function isPaymentTokenAllowed(address paymentToken) external view returns (bool);

    // ------------------ External Mutable Functions ------------------
    /// @dev Sets constant with uint256 value
    /// @param constantType Constant type
    /// @param v New uint256 value
    function setConstant(ConstantType constantType, uint256 v) external;

    /// @dev Returns all chain constants
    /// @return all chain constants
    function getAllConstants() external view returns (GlobalConstants memory);
}

