pragma solidity ^0.8.19;

import {IDeal} from "src/deal/v1/interfaces/IDeal.sol";
import {CIDV1} from "src/utils/Common.sol";

/// @title Market contract interface
/// @dev Market contract is responsible for managing the deals and offers
interface IMarket {
    // ------------------ Errors ------------------
    /// @dev Throws if the min workers of the deal is not matched
    /// @param _minWorkers The min workers of the deal
    error MinWorkersNotMatched(uint256 _minWorkers);

    // ----------------- Events -----------------
    /// @dev Emitted when a deal is matched with a compute unit
    /// @param peerId The peer id of the deal
    /// @param deal The deal address
    /// @param onchainWorkerId onchain worker id, for use in tools that interact with chain
    /// @param cuIds ids of compute units matched
    /// @param appCID The app cid of the deal
    event ComputeUnitsMatched(
        bytes32 indexed peerId,
        IDeal deal,
        bytes32 onchainWorkerId,
        bytes32[] cuIds,
        CIDV1 appCID
    );

    /// @dev Emitted when a compute unit removed from a peer
    /// @param cuIds The list of compute unit ids
    /// @param deal The deal address
    /// @param peerId The peer id
    event ComputeUnitsRemovedFromDeal(bytes32 indexed peerId, IDeal indexed deal, bytes32[] cuIds, bytes32 onchainWorkerId);


    /// @dev Emitted when a worker is activated
    /// @param peer_id The peer id
    /// @param deal The deal address
    /// @param onchain_worker_id The onchain worker id
    /// @param offchain_worker_id The offchain worker id
    event WorkerActivated(bytes32 indexed peer_id, IDeal indexed deal, bytes32 onchain_worker_id, bytes32 offchain_worker_id);


    /**
     * @dev Match Deal with Compute Units provided.
     * @notice Match deal with offers and compute units (peers checks through compute units).
     * @notice It validates provided CUs and silently ignore unvalidated ones on checking:
     * @notice - deal.maxWorkersPerProvider and silently ignore other CUs out of this limit.
     * @notice - Offer, or Peer not allowed to match (deal.isProviderAllowed, allowed prices, paymentToken, effectors)
     * @notice - Compute Unit (CU): Active CC status, also note, protocol does not allow more than one CU per peer
     * @notice    for the same Deal. But, when deal consists of whitelisted provider - CUs of this Provider
     * @notice    could be matched without even CC.
     * @notice TODO: consolidate workaround: when it comes to check CC status, on wrong status the whole transaction
     * @notice  will be failed instead of to be silenced.
     * @dev There should be `bytes32[][] calldata peers` as well, but it is not supported by subgraph codegen.
     * @dev  Ref to https://github.com/graphprotocol/graph-tooling/issues/342.
     * @notice This method is mirrored in ts-client/src/dealMatcherClient/dealMatcherClient.ts.
     * @param deal: Deal to match.
     * @param offers: Offers array that represents offers in computeUnits 3D array.
     * @param computeUnits: Compute Units per offer id (3D array: provider -> peer -> CU) to match with.
     */
    function matchDeal(IDeal deal, bytes32[] calldata offers, bytes32[][][] calldata computeUnits) external;

    // Unit management
    /// @dev Return resources from deal
    function moveResourcesFromDeal(bytes32[] memory unitIds, bytes32 onchainWorkerId) external;

    /// @dev Report that a worker is activated, only throws event
    /// @param peerId The peer id
    /// @param onchainWorkerId The onchain worker id
    /// @param offchainWorkerId The offchain worker id
    function reportWorkerActivated(bytes32 peerId, bytes32 onchainWorkerId, bytes32 offchainWorkerId) external;
}

