pragma solidity ^0.8.19;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IWorkerManagerV2} from "src/deal/interfaces/IWorkerManager.sol";
import {ICore} from "src/core/interfaces/ICore.sol";
import {IDiamond} from "src/interfaces/IDiamond.sol";
import {IBalanceKeeper} from "src/balance-keeper/IBalanceKeeper.sol";
import {IOfferV2} from "src/core/interfaces/IOfferV2.sol";
import {CIDV1} from "src/utils/Common.sol";

interface IDealV2 is IWorkerManagerV2 {
    // ------------------ Types ------------------
    enum Status {
        // the deal does have enough funds to pay for the workers
        INSUFFICIENT_FUNDS,
        ACTIVE,
        // the deal is stopped
        ENDED,
        // the deal has a balance and waiting for workers
        NOT_ENOUGH_WORKERS,
        // the deal has balance less than the minimal balance. Min balance: 2 * targetWorkers * pricePerCuPerEpoch * cuCountPerWorker
        SMALL_BALANCE
    }

    struct WorkerPaymentInfo {
        uint256 snapshotEpoch;
        uint256 gapsDelta;
    }

    struct DealStorage {
        uint256 totalBalance;
        uint256 lockedBalance;
        uint256 gapsEpochCount;
        uint256 maxPaidEpoch;
        uint256 lastCommitedEpoch;
        mapping(bytes32 => WorkerPaymentInfo) workerPaymentInfo;
        uint256 endedEpoch;
        uint256 protocolVersion;
        uint256 migratedToBalanceKeeper; // default 0, will be set to 1 after migration
    }

    struct DealRewards {
        uint256 providerReward;
        uint256 stakerReward;
    }

    // ----------------- Events -----------------
    /// @dev Emitted when a owner deposits payment token to the deal
    event Deposited(uint256 amount);

    /// @dev Emitted when a owner withdraws payment token from the deal
    event Withdrawn(uint256 amount);

    /// @dev Emitted when a compute unit withdraws rewards from the deal
    event RewardWithdrawn(bytes32 onchainWorkerId, uint256 providerReward, uint256 stakerReward);

    /// @dev Emitted when a max paid epoch is updated
    event MaxPaidEpochUpdated(uint256 maxPaidEpoch);

    /// @dev Emitted when a deal is ended. This event can be emitted in the not-ended epoch. It can be emitted after the ended epoch in any epoch
    event DealEnded(uint256 endedEpoch);

    // ------------------ Init ------------------
    function initialize(
        IDiamond diamond_,
        CIDV1 calldata appCID_,
        IERC20 paymentToken_,
        uint256 targetWorkers_,
        IOfferV2.ResourceRequirement[] calldata resources_,
        uint256 pricePerEpoch_,
        uint256 maxWorkersPerProvider_,
        AccessTypeV2 providersAccessType_,
        address[] calldata providersAccessList_,
        uint256 protocolVersion_,
        address owner_
    ) external;

    /// @dev Returns the status of the deal
    function getStatus() external view returns (Status);

    /// @dev Returns the free balance of the deal
    function getFreeBalance() external view returns (uint256);

    /// @dev Returns the reward amount by compute unit ID
    function getRewardAmount(bytes32 onchainWorkerId) external view returns (DealRewards memory);

    /// @dev Returns the max paid epoch
    function getMaxPaidEpoch() external view returns (uint256);

    /// @dev Returns the protocol version
    function getProtocolVersion() external view returns (uint256);

    /// @dev Deposits payment token to the deal
    function depositFromOwner(uint256 amount, IBalanceKeeper.DealDepositReason reason) external;

    /// @dev Withdraws payment token from the deal
    function withdraw(uint256 amount) external;

    // ------------------ Public Mutable Functions ------------------
    /// @dev Adds a compute unit to the deal
    /// @param computeProvider The compute provider address
    /// @param workerData The worker with data
    /// @param peerId The peer ID
    function addWorker(address computeProvider, WorkerDataV2 memory workerData, bytes32 peerId) external;

    /// @dev Removes a worker from the deal
    function removeWorker(bytes32 onchainWorkerId) external;

    /// @dev Allows oracle to remove worker from a deal that is not paying workers.
    function removeStaleWorkers(bytes32[] calldata onchainWorkerIds) external;

    /// @dev Withdraws rewards from the deal by compute unit ID
    function withdrawRewards(bytes32 onchainWorkerId) external;

    /// @dev Set offchain worker ID for a corresponding onchain worker for a deal
    function activateWorker(bytes32 onchainWorkerId, bytes32 offchainWorkerId) external;

    /// @dev Stop the deal
    function stop() external;
}

