 */

pragma solidity ^0.8.0;

import {IBaseOffer} from "src/core/interfaces/IBaseOffer.sol";
import {IOffer} from "src/core/interfaces/IOffer.sol";
import {LibBaseOffer, OfferStorage} from "src/lib/LibBaseOffer.sol";
import {LibCapacity} from "src/lib/LibCapacity.sol";
import {LibDiamond} from "src/lib/LibDiamond.sol";
import {LibGlobalConst} from "src/lib/LibGlobalConst.sol";
import {IDeal} from "src/deal/v1/interfaces/IDeal.sol";
import {IWorkerManager} from "src/deal/v1/interfaces/IWorkerManager.sol";
import {CIDV1} from "src/utils/Common.sol";
import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import {OwnableUpgradableDiamond} from "src/utils/OwnableUpgradableDiamond.sol";

library LibOffer {
    using EnumerableSet for EnumerableSet.Bytes32Set;

    function store() internal pure returns (OfferStorage storage s) {
        return LibBaseOffer.store();
    }

    // mainly for IOffer impl
    function registerMarketOffer(
        uint256 minPricePerCuPerEpoch,
        address paymentToken,
        CIDV1[] calldata effectors,
        IOffer.RegisterComputePeer[] calldata peers,
        uint256 minProtocolVersion,
        uint256 maxProtocolVersion
    ) internal returns (bytes32) {
        require(minProtocolVersion == LibBaseOffer.PROTOCOL_V1 && maxProtocolVersion == LibBaseOffer.PROTOCOL_V1, "Offer: protocol version mismatch, required v1");

        bytes32 offerId = LibBaseOffer.registerBaseOffer(paymentToken, LibBaseOffer.PROTOCOL_V1, LibBaseOffer.PROTOCOL_V1);

        // set V1 - only data
        addEffector(offerId, effectors);
        changeMinPricePerCuPerEpoch(offerId, minPricePerCuPerEpoch);

        addComputePeers(offerId, peers);

        return offerId;
    }

    // can be common
    function removeOffer(bytes32 offerId) internal {
        // effectors data is automatically cleaned
        LibBaseOffer.removeBaseOffer(offerId);
    }

    // v1 only
    function addComputePeers(bytes32 offerId, IOffer.RegisterComputePeer[] calldata peers) internal {
        for (uint256 peerIndex = 0; peerIndex < peers.length; ++peerIndex) {
            IOffer.RegisterComputePeer calldata peer = peers[peerIndex];
            LibBaseOffer.registerBaseComputePeer(offerId, peer.peerId, peer.owner, peer.unitIds);
        }
    }

    // can be common
    function removeComputePeer(bytes32 peerId) internal {
        LibBaseOffer.removeBaseComputePeer(peerId);
    }

    // can be common
    function addComputeUnits(bytes32 peerId, bytes32[] calldata unitIds) internal {
        LibBaseOffer.registerBaseComputeUnits(peerId, unitIds);
    }

    // can be common
    function removeComputeUnit(bytes32 unitId) internal {
        LibBaseOffer.removeBaseComputeUnit(unitId);
    }

    // Change offer
    // v1 only
    function changeMinPricePerCuPerEpoch(bytes32 offerId, uint256 newMinPricePerCuPerEpoch) internal {
        OfferStorage storage offerStorage = store();
        IOffer.Offer storage offer = offerStorage.offers[offerId];

        require(offer.provider == msg.sender, "Offer: only provider can call this method");
        require(newMinPricePerCuPerEpoch > 0, "Min price per CU per epoch should be greater than 0");

        offer.minPricePerCuPerEpoch = newMinPricePerCuPerEpoch;

        emit IOffer.MinPricePerCuPerEpochUpdated(offerId, newMinPricePerCuPerEpoch);
    }

    // v1 only
    function addEffector(bytes32 offerId, CIDV1[] calldata newEffectors) internal {
        OfferStorage storage offerStorage = store();
        IOffer.Offer storage offer = offerStorage.offers[offerId];

        require(offer.provider == msg.sender, "Offer: only provider can call this method");

        for (uint256 i = 0; i < newEffectors.length; i++) {
            CIDV1 calldata effectorCID = newEffectors[i];
            bytes32 effectorHash = _hashEffectorCID(effectorCID);

            require(!offerStorage.effectorsByOfferId[offerId].hasEffector[effectorHash], "Effector already exists");

            offerStorage.effectorsByOfferId[offerId].hasEffector[effectorHash] = true;

            emit IOffer.EffectorAdded(offerId, effectorCID);
        }
    }

    // v1 only
    function removeEffector(bytes32 offerId, CIDV1[] calldata effectors) internal {
        OfferStorage storage offerStorage = store();
        IOffer.Offer storage offer = offerStorage.offers[offerId];

        require(offer.provider == msg.sender, "Offer: only provider can call this method");

        for (uint256 i = 0; i < effectors.length; i++) {
            CIDV1 calldata effectorCID = effectors[i];
            bytes32 effectorHash = _hashEffectorCID(effectorCID);

            require(offerStorage.effectorsByOfferId[offerId].hasEffector[effectorHash], "Effector doesn't exist");

            offerStorage.effectorsByOfferId[offerId].hasEffector[effectorHash] = false;

            emit IOffer.EffectorRemoved(offerId, effectorCID);
        }
    }

    // Effector info
    // v1 only
    function setEffectorInfo(CIDV1 calldata id, string calldata description, CIDV1 calldata metadata)
    internal
    {
        LibDiamond.enforceIsContractOwner();
        OfferStorage storage offerStorage = store();
        require(bytes(description).length > 0, "Description should be not empty");

        bytes32 effectorHash = _hashEffectorCID(id);
        IOffer.EffectorInfo storage effectorInfo = offerStorage.effectorInfoById[effectorHash];

        effectorInfo.description = description;
        effectorInfo.metadata = metadata;

        emit IOffer.EffectorInfoSet(id, description, metadata);
    }

    function getEffectorInfo(CIDV1 calldata id) internal view returns (IOffer.EffectorInfo memory) {
        return store().effectorInfoById[_hashEffectorCID(id)];
    }

    // v1 only
    function removeEffectorInfo(CIDV1 calldata id) internal {
        LibDiamond.enforceIsContractOwner();
        OfferStorage storage offerStorage = store();

        bytes32 effectorHash = _hashEffectorCID(id);
        IOffer.EffectorInfo storage effectorInfo = offerStorage.effectorInfoById[effectorHash];

        require(bytes(effectorInfo.description).length > 0, "Effector info doesn't exist");

        delete offerStorage.effectorInfoById[effectorHash];

        emit IOffer.EffectorInfoRemoved(id);
    }

    function getOffer(bytes32 offerId) internal view returns (IOffer.Offer memory) {
        IOffer.Offer storage offer = store().offers[offerId];
        require(offer.provider != address(0x00), "Offer: offer doesn't exist");

        return offer;
    }

    function _getOffer(bytes32 offerId) internal view returns (IOffer.Offer storage) {
        return store().offers[offerId];
    }

    function _hasOfferEffectors(bytes32 offerId, CIDV1[] memory effectors) internal view returns (bool) {
        OfferStorage storage offerStorage = store();

        for (uint256 i = 0; i < effectors.length; i++) {
            bytes32 effectorHash = _hashEffectorCID(effectors[i]);

            if (!offerStorage.effectorsByOfferId[offerId].hasEffector[effectorHash]) {
                return false;
            }
        }

        return true;
    }

    function _hashEffectorCID(CIDV1 memory effectorCID) internal pure returns (bytes32) {
        return keccak256(abi.encodePacked(effectorCID.prefixes, effectorCID.hash));
    }

    function cleanupV1Storage(bytes32 offerId) internal {
        store().offers[offerId].minPricePerCuPerEpoch = 0;
        delete store().effectorsByOfferId[offerId];
    }
}

