 * along with this program.  If not, see <https://www.gnu.org/licenses/>.
 */

pragma solidity ^0.8.0;

import {IBaseOffer} from "src/core/interfaces/IBaseOffer.sol";
import {IOfferV2} from "src/core/interfaces/IOfferV2.sol";
import {LibBaseOffer} from "src/lib/LibBaseOffer.sol";
import {EnumerableSet} from "@openzeppelin/contracts//utils/structs/EnumerableSet.sol";
import {LibOffer} from "src/lib/LibOffer.sol";

struct OfferStorageV2 {
    mapping(bytes32 offerId => IOfferV2.OfferResourcesParams) offerParamsById;
    mapping(bytes32 peerId => IOfferV2.PeerResourceParams) peerResourcesById;
    mapping(bytes32 resourceId => IOfferV2.ResourceDescription) resources;
    EnumerableSet.Bytes32Set resourceIds;

    EnumerableSet.Bytes32Set offers;
    mapping(bytes32 offerId => EnumerableSet.Bytes32Set) peers;

    // for future methods to register datacenters and attach them to offers
    mapping(bytes32 => IOfferV2.Datacenter) datacenters;
    mapping(bytes32 offerId => bytes32 datacenterId) offerDatacenterByID;
    EnumerableSet.Bytes32Set datacenterIds;
}

library LibOfferV2 {
    uint256 constant RAM_PER_VCPU = 2 * GiB / BYTES_PER_RAM;
    uint256 constant VCPU_PER_CU = 2;
    uint256 constant BYTES_PER_RAM = 1 * MiB;
    uint256 constant BYTES_PER_STORAGE = 1 * MiB;
    uint256 constant MiB = 1024 * 1024;
    uint256 constant GiB = 1024 * MiB;

    using EnumerableSet for EnumerableSet.Bytes32Set;
    bytes32 private constant _STORAGE_SLOT = bytes32(uint256(keccak256("fluence.market.storage.v2")) - 1);

    function storeV2() internal pure returns (OfferStorageV2 storage s) {
        bytes32 storageSlot = _STORAGE_SLOT;
        assembly {
            s.slot := storageSlot
        }
    }

    function getResourcePrice(bytes32 offerId, bytes32 resourceId) internal view returns(uint256) {
        IOfferV2.OfferResourcesParams storage offerResourceParams = storeV2().offerParamsById[offerId];

        require(offerResourceParams.resourceIds.contains(resourceId), "LibOfferV2: offer does not have this resource");
        return offerResourceParams.resourcePrices[resourceId];
    }

    function _getPeerResource(bytes32 peerId, bytes32 resourceId) internal view returns(IOfferV2.PeerResource storage) {
        IOfferV2.PeerResourceParams storage peerResourceParams = storeV2().peerResourcesById[peerId];
        require(peerResourceParams.resourceIds.contains(resourceId), "LibOfferV2: peer does not have this resource");

        return peerResourceParams.resources[resourceId];
    }

    function upgradeMarketOfferToV2(
        bytes32 offerId,
        IOfferV2.RegisterResourcePrice[] calldata resourcePrices,
        IOfferV2.UpgradeComputePeerV2[] calldata peers,
        bytes32 datacenterId
    ) internal {
        LibOffer.cleanupV1Storage(offerId);
        LibBaseOffer.upgradeProtocolVersion(offerId, LibBaseOffer.PROTOCOL_V2, LibBaseOffer.PROTOCOL_V2);
        IBaseOffer.BaseOffer memory offer = LibBaseOffer.getBaseOffer(offerId);
        require(offer.peerCount == peers.length, "LibOfferV2: cannot upgrade offer without traversing through all peers");
        _registerOfferPrices(offerId, resourcePrices);
        storeV2().offers.add(offerId);

        for (uint256 peerIndex = 0; peerIndex < peers.length; peerIndex++) {
            IOfferV2.UpgradeComputePeerV2 calldata peerToRegister = peers[peerIndex];
            IBaseOffer.ComputePeer memory peer = LibBaseOffer.getComputePeer(peerToRegister.peerId);
            require(peer.offerId == offerId, "LibOfferV2: peer does not belong to the given offer");
            storeV2().peers[offerId].add(peerToRegister.peerId);

            _registerPeerResources(peerToRegister.peerId, peerToRegister.resources);
        }

        setOfferDatacenter(offerId, datacenterId);
    }

    function registerMarketOfferV2(
        address paymentToken,
        IOfferV2.RegisterResourcePrice[] calldata resourcePrices,
        IOfferV2.RegisterComputePeerV2[] calldata peers,
        bytes32 datacenterId
    ) internal returns (bytes32) {
        OfferStorageV2 storage offerStorageV2 = storeV2();
        bytes32 offerId = LibBaseOffer.registerBaseOffer(paymentToken, LibBaseOffer.PROTOCOL_V2, LibBaseOffer.PROTOCOL_V2);

        offerStorageV2.offers.add(offerId);
        emit IOfferV2.OfferV2Created(offerId, paymentToken, msg.sender, datacenterId);

        _registerOfferPrices(offerId, resourcePrices);
        registerComputePeersV2(offerId, peers);
        setOfferDatacenter(offerId, datacenterId);

        return offerId;
    }

    function registerComputePeersV2(bytes32 offerId, IOfferV2.RegisterComputePeerV2[] calldata peers) internal {
        for (uint256 peerIndex = 0; peerIndex < peers.length; ++peerIndex) {
            registerComputePeerV2(offerId, peers[peerIndex]);
        }
    }

    function registerComputePeerV2(bytes32 offerId, IOfferV2.RegisterComputePeerV2 calldata peerData) internal {
        // Peer and computeUnits are registered here
        OfferStorageV2 storage offerStorageV2 = storeV2();
        LibBaseOffer.registerBaseComputePeer(offerId, peerData.peerId, peerData.owner, peerData.unitIds);

        offerStorageV2.peers[offerId].add(peerData.peerId);

        _registerPeerResources(peerData.peerId, peerData.resources);
    }

    function _registerOfferPrices(bytes32 offerId, IOfferV2.RegisterResourcePrice[] calldata resourcePrices) internal {
        OfferStorageV2 storage offerStorageV2 = LibOfferV2.storeV2();
        IOfferV2.OfferResourcesParams storage offerResources = offerStorageV2.offerParamsById[offerId];

        for (uint256 priceIndex = 0; priceIndex < resourcePrices.length; ++priceIndex) {
            IOfferV2.RegisterResourcePrice calldata priceData = resourcePrices[priceIndex];
            require(priceData.price > 0, "LibOfferV2: cannot register 0 as price for a resource"); // TODO we can remove this check if it is not needed
            require(offerStorageV2.resourceIds.contains(priceData.resourceId), "LibOfferV2: Attempt to register a resource not known to the network");
            offerResources.resourcePrices[priceData.resourceId] = priceData.price;
            bool addedNewEntry = offerResources.resourceIds.add(priceData.resourceId);
            require(addedNewEntry, "LibOfferV2: resource was already registered in offer");

            emit IOfferV2.ResourcePriceUpdated(offerId, priceData.resourceId, priceData.price);
        }
    }

    function _registerPeerResources(bytes32 peerId, IOfferV2.RegisterPeerResource[] calldata resources) internal {
        OfferStorageV2 storage offerStorageV2 = LibOfferV2.storeV2();
        IBaseOffer.ComputePeer memory peer = LibBaseOffer.getComputePeer(peerId);

        IOfferV2.OfferResourcesParams storage offerResources = offerStorageV2.offerParamsById[peer.offerId];
        IOfferV2.PeerResourceParams storage peerResources = offerStorageV2.peerResourcesById[peerId];

        for (uint256 resourceIndex = 0; resourceIndex < resources.length; ++resourceIndex) {
            IOfferV2.RegisterPeerResource calldata resource = resources[resourceIndex];

            // todo check existence differently
            require(offerResources.resourcePrices[resource.resourceId] > 0, "LibOfferV2: attempt to register a resource to peer without a price");

            _registerPeerResource(peerId, peerResources, resource);
        }

        enforceResourceInvariants(peerId);
    }

    function changeResourcePrice(bytes32 offerId, bytes32 resourceId, uint256 newPrice) internal {
        IOfferV2.OfferResourcesParams storage offerParams = storeV2().offerParamsById[offerId];

        require(newPrice != 0, "LibOfferV2: cannot change resource price to zero");

        // this function automatically adds new resource to offer, if it wasn't there
        offerParams.resourceIds.add(resourceId);
        offerParams.resourcePrices[resourceId] = newPrice;

        emit IOfferV2.ResourcePriceUpdated(offerId, resourceId, newPrice);
    }

    function changeResourceMaxSupply(bytes32 peerId, bytes32 resourceId, uint256 newMaxSupply) internal {
        IOfferV2.PeerResourceParams storage peerResourceParams = storeV2().peerResourcesById[peerId];
        IOfferV2.PeerResource storage peerResource = peerResourceParams.resources[resourceId];

        // todo check existence differently
        require(peerResourceParams.resourceIds.contains(resourceId), "LibOfferV2: peer resource is not registered");

        if (peerResource.maxSupply > newMaxSupply) {
            uint256 maxSupplyDecrease = peerResource.maxSupply - newMaxSupply;
            require(peerResource.availableSupply >= maxSupplyDecrease, "LibOfferV2: cannot decrease peer resource supply because it is used in Deals");

            peerResource.availableSupply -= maxSupplyDecrease;
            peerResource.maxSupply -= maxSupplyDecrease;
        } else {
            uint256 maxSupplyIncrease = newMaxSupply - peerResource.maxSupply;
            peerResource.availableSupply += maxSupplyIncrease;
            peerResource.maxSupply += maxSupplyIncrease;
        }

        enforceResourceInvariants(peerId);
        emit IOfferV2.PeerResourceSupplyUpdated(peerId, resourceId, peerResource.availableSupply, peerResource.maxSupply);
    }

    function enforceResourceInvariants(bytes32 peerId) internal view {
        uint256 vcpuAvailableSupply = 0;
        uint256 vcpuMaxSupply = 0;
        uint256 ramAvailableSupply = 0;
        uint256 ramMaxSupply = 0;

        IBaseOffer.ComputePeer storage peer = LibBaseOffer.store().peers[peerId];
        IOfferV2.PeerResourceParams storage peerResources = storeV2().peerResourcesById[peerId];
        uint256 peerResourcesLength = peerResources.resourceIds.length();
        for (uint256 resourceIndex = 0; resourceIndex < peerResourcesLength; ++resourceIndex) {
            bytes32 resourceId = peerResources.resourceIds.at(resourceIndex);
            IOfferV2.ResourceDescription storage resourceData = storeV2().resources[resourceId];
            IOfferV2.PeerResource storage peerResource = peerResources.resources[resourceId];
            if (resourceData.ty == IOfferV2.ResourceType.VCPU) {
                vcpuAvailableSupply += peerResource.availableSupply;
                vcpuMaxSupply += peerResource.maxSupply;
            } else if (resourceData.ty == IOfferV2.ResourceType.RAM) {
                ramAvailableSupply += peerResource.availableSupply;
                ramMaxSupply += peerResource.maxSupply;
            } else {
                continue;
            }
        }


        require(vcpuAvailableSupply * RAM_PER_VCPU <= ramAvailableSupply, "LibOfferV2: broken invariant, not enough RAM to support VCPU (available)");
        require(vcpuMaxSupply * RAM_PER_VCPU <= ramMaxSupply, "LibOfferV2: broken invariant, not enough RAM to support VCPU (max)");
        require(vcpuMaxSupply == peer.unitCount * VCPU_PER_CU, "LibOfferV2: broken invariant, VCPU number does not match CU count");
    }

    function addComputeUnitsV2(bytes32 peerId, bytes32[] calldata unitIds, IOfferV2.RegisterPeerResource calldata resource) internal {
        LibBaseOffer.registerBaseComputeUnits(peerId, unitIds);

        IOfferV2.ResourceDescription storage resourceData = storeV2().resources[resource.resourceId];
        IBaseOffer.ComputePeer memory peer = LibBaseOffer.getComputePeer(peerId);
        IOfferV2.OfferResourcesParams storage offerResources = storeV2().offerParamsById[peer.offerId];

        require(resourceData.ty == IOfferV2.ResourceType.VCPU, "LibOfferV2: registering compute units requires adding new VCPU resources");
        require(resource.supply == unitIds.length * VCPU_PER_CU, "LibOfferV2: compute units count and resource supply mismatch");

        require(offerResources.resourceIds.contains(resource.resourceId), "LibOfferV2: attempt to register a resource to peer without a price");

        IOfferV2.PeerResourceParams storage peerResources = storeV2().peerResourcesById[peerId];

        if (peerResources.resourceIds.contains(resource.resourceId)) {
            uint256 oldSupply = _getPeerResource(peerId, resource.resourceId).maxSupply;
            changeResourceMaxSupply(peerId, resource.resourceId, oldSupply + resource.supply);
        } else {
            _registerPeerResource(peerId, peerResources, resource);
        }

        enforceResourceInvariants(peerId);
    }

    function removeOfferV2(bytes32 offerId) internal {
        OfferStorageV2 storage offerStorageV2 = storeV2();
        LibBaseOffer.removeBaseOffer(offerId);

        // no need to throw event because indexers can cleanup themselves
        offerStorageV2.offers.remove(offerId);
        delete offerStorageV2.peers[offerId];
        delete offerStorageV2.offerParamsById[offerId];
        delete offerStorageV2.offerDatacenterByID[offerId];
    }

    function removeComputePeer(bytes32 peerId) internal {
        OfferStorageV2 storage offerStorageV2 = storeV2();
        bytes32 offerId = LibBaseOffer.getComputePeer(peerId).offerId;

        LibBaseOffer.removeBaseComputePeer(peerId);

        offerStorageV2.peers[offerId].remove(peerId);

        // no need to throw event because indexers can cleanup themselves
        delete offerStorageV2.peerResourcesById[peerId];
    }

    function removeComputeUnit(bytes32 unitId, bytes32 resourceId) internal {
        IBaseOffer.ComputeUnit memory unit = LibBaseOffer.getComputeUnit(unitId);
        LibBaseOffer.removeBaseComputeUnit(unitId);
        uint256 peerResourceSupply = storeV2().peerResourcesById[unit.peerId].resources[resourceId].maxSupply;
        IOfferV2.ResourceDescription storage resourceDescription = storeV2().resources[resourceId];
        require(peerResourceSupply >= VCPU_PER_CU, "LibOfferV2: not enough resource in peer");
        require(resourceDescription.ty == IOfferV2.ResourceType.VCPU, "LibOfferV2: required to remove a VCPU resource along with compute unit");

        changeResourceMaxSupply(unit.peerId, resourceId, peerResourceSupply - VCPU_PER_CU);
    }

    function registerResource(IOfferV2.ResourceType ty, string calldata metadata) internal returns(bytes32) {
        OfferStorageV2 storage offerStorageV2 = storeV2();
        bytes32 resourceId = bytes32(offerStorageV2.resourceIds.length() + 1);
        offerStorageV2.resourceIds.add(resourceId);
        offerStorageV2.resources[resourceId] = IOfferV2.ResourceDescription(
            ty,
            metadata
        );

        emit IOfferV2.ResourceCreated(resourceId, ty, metadata);

        return resourceId;
    }

    function updateResourceMetadata(bytes32 resourceId, string calldata metadata) internal {
        require(storeV2().resourceIds.contains(resourceId), "LibOfferV2: resource does not exist in the network");
        storeV2().resources[resourceId].metadata = metadata;
        emit IOfferV2.ResourceMetadataUpdated(resourceId, metadata);
    }

    // Does not check invariant VCPU/RAM
    function _registerPeerResource(bytes32 peerId, IOfferV2.PeerResourceParams storage peerResources, IOfferV2.RegisterPeerResource calldata resource) internal {
        peerResources.resources[resource.resourceId] = IOfferV2.PeerResource({
            availableSupply: resource.supply,
            maxSupply: resource.supply,
            details: resource.details
        });

        bool addedNewEntry = peerResources.resourceIds.add(resource.resourceId);
        require(addedNewEntry, "LibOfferV2: peer resource was already registered");
        emit IOfferV2.PeerResourceRegistered(peerId, resource.resourceId, resource.supply, resource.details);
    }

    function removePeerResource(bytes32 peerId, bytes32 resourceId) internal {
        IOfferV2.PeerResourceParams storage peerResourceParams = storeV2().peerResourcesById[peerId];

        require(storeV2().resourceIds.contains(resourceId), "LibOfferV2: resource does not exist in the network");
        require(peerResourceParams.resourceIds.contains(resourceId), "LibOfferV2: peer does not have this resource");

        delete peerResourceParams.resources[resourceId];
        peerResourceParams.resourceIds.remove(resourceId);

        LibOfferV2.enforceResourceInvariants(peerId);
        emit IOfferV2.PeerResourceRemoved(peerId, resourceId);
    }

    function registerPeerResource(bytes32 peerId, IOfferV2.RegisterPeerResource calldata resource) internal {
        bytes32 offerId = LibBaseOffer.getComputePeer(peerId).offerId;
        require(storeV2().offerParamsById[offerId].resourceIds.contains(resource.resourceId), "LibOfferV2: resource is not registered in offer");

        IOfferV2.PeerResourceParams storage peerResourceParams = storeV2().peerResourcesById[peerId];
        _registerPeerResource(peerId, peerResourceParams, resource);
        LibOfferV2.enforceResourceInvariants(peerId);
    }

    function changeResourceDetails(bytes32 peerId, bytes32 resourceId, string calldata details) internal {
        IOfferV2.PeerResourceParams storage peerResourceParams = storeV2().peerResourcesById[peerId];

        require(storeV2().resourceIds.contains(resourceId), "LibOfferV2: resource does not exist in the network");
        require(peerResourceParams.resourceIds.contains(resourceId), "LibOfferV2: peer does not have this resource");
        peerResourceParams.resources[resourceId].details = details;
        emit IOfferV2.PeerResourceDetailsUpdated(peerId, resourceId, details);
    }

    function enforceDealResourceInvariants(IOfferV2.ResourceRequirement[] calldata resources) internal view {
        uint256 vcpuRequired = 0;
        uint256 ramRequired = 0;

        for (uint256 resourceIndex = 0; resourceIndex < resources.length; ++resourceIndex) {
            IOfferV2.ResourceRequirement calldata resource = resources[resourceIndex];
            IOfferV2.ResourceDescription storage resourceData = storeV2().resources[resource.resourceId];
            if (resourceData.ty == IOfferV2.ResourceType.VCPU) {
                vcpuRequired += resource.quantity;
            } else if (resourceData.ty == IOfferV2.ResourceType.RAM) {
                ramRequired += resource.quantity;
            } else {
                continue;
            }
        }

        require(vcpuRequired > 0, "LibOfferV2: deal with broken invariant: zero VCPUs requested");
        require(vcpuRequired % VCPU_PER_CU == 0, "LibOfferV2: deal with broken invariant: number of VCPUs requested corresponds to a fractional number of CUs");
        require(vcpuRequired * RAM_PER_VCPU <= ramRequired, "LibOfferV2: deal with broken invariant: not enough RAM requested to support requested number of VCPUs");
    }

    function createDatacenter(IOfferV2.RegisterDatacenter calldata datacenter) internal returns(bytes32) {
        bytes32 id = bytes32(storeV2().datacenterIds.length() + 1);
        storeV2().datacenters[id] = IOfferV2.Datacenter(
            id,
            datacenter.countryCode,
            datacenter.cityCode,
            datacenter.index,
            datacenter.tier,
            datacenter.certifications
        );
        storeV2().datacenterIds.add(id);

        emit IOfferV2.DatacenterCreated(id, datacenter.countryCode,datacenter.cityCode, datacenter.index,datacenter.tier, datacenter.certifications);
        return id;
    }

    function updateDatacenter(bytes32 id, uint256 tier, string[] calldata certifications) internal {
        require(storeV2().datacenterIds.contains(id), "LibOfferV2: datacenter does not exist in the network");
        IOfferV2.Datacenter storage datacenter = storeV2().datacenters[id];
        datacenter.tier = tier;
        datacenter.certifications = certifications;
        emit IOfferV2.DatacenterUpdated(id, tier, certifications);
    }

    function setOfferDatacenter(bytes32 offerId, bytes32 datacenterId) internal {
        require(storeV2().datacenterIds.contains(datacenterId), "LibOfferV2: datacenter does not exist");
        storeV2().offerDatacenterByID[offerId] = datacenterId;
        // TODO: maybe do not send event if DC ID is zero?
        emit IOfferV2.OfferDatacenterUpdated(offerId, datacenterId);
    }

    function getOfferDatacenter(bytes32 offerId) internal view returns(bytes32) {
        return storeV2().offerDatacenterByID[offerId];
    }
}

library LibOfferV2View {
    using EnumerableSet for EnumerableSet.Bytes32Set;

    function storeV2() internal pure returns (OfferStorageV2 storage s) {
        s = LibOfferV2.storeV2();
    }

    function getResources() internal view returns(IOfferV2.ResourceDescriptionView[] memory) {
        uint256 resourcesLength = storeV2().resourceIds.length();
        IOfferV2.ResourceDescriptionView[] memory resources = new IOfferV2.ResourceDescriptionView[](resourcesLength);
        for (uint256 resourceIndex = 0; resourceIndex < resourcesLength; resourceIndex++) {
            bytes32 resourceId = storeV2().resourceIds.at(resourceIndex);
            IOfferV2.ResourceDescription storage desc = storeV2().resources[resourceId];
            resources[resourceIndex] = IOfferV2.ResourceDescriptionView({
                id: resourceId,
                ty: desc.ty,
                metadata: desc.metadata
            });
        }

        return resources;
    }

    function getResourceDescription(bytes32 resourceId) internal view returns (IOfferV2.ResourceDescription storage) {
        OfferStorageV2 storage offerStorageV2 = storeV2();
        require(offerStorageV2.resourceIds.contains(resourceId), "LibOfferV2: resourceId does not exist");

        return offerStorageV2.resources[resourceId];
    }


    function getOffers() internal view returns(IOfferV2.OfferV2View[] memory) {
        OfferStorageV2 storage offerV2Storage = storeV2();
        bytes32[] memory offerIds = offerV2Storage.offers.values();
        IOfferV2.OfferV2View[] memory offerViews = new IOfferV2.OfferV2View[](offerIds.length);

        for (uint256 offerIndex; offerIndex < offerIds.length; offerIndex++) {
            bytes32 offerId = offerIds[offerIndex];
            offerViews[offerIndex] = getOffer(offerId);
        }

        return offerViews;
    }

    function getOffer(bytes32 offerId) internal view returns(IOfferV2.OfferV2View memory) {
        IBaseOffer.BaseOffer memory baseOffer = LibBaseOffer.getBaseOffer(offerId);
        return IOfferV2.OfferV2View({
            offerId: offerId,
            provider: baseOffer.provider,
            paymentToken: baseOffer.paymentToken,
        //datacenterId: storeV2().offerDatacenterByID[offerId], //todo unlock when breaking api
            prices: getOfferPrices(offerId)
        });
    }

    function getOfferPrices(bytes32 offerId) internal view returns(IOfferV2.OfferV2PriceView[] memory) {
        OfferStorageV2 storage offerV2Storage = storeV2();
        IOfferV2.OfferResourcesParams storage offerResourceParams = offerV2Storage.offerParamsById[offerId];
        bytes32[] memory resourceIds = offerResourceParams.resourceIds.values();
        IOfferV2.OfferV2PriceView[] memory offerPriceViews = new IOfferV2.OfferV2PriceView[](resourceIds.length);

        for (uint256 resourceIndex; resourceIndex < resourceIds.length; resourceIndex++) {
            bytes32 resourceId = resourceIds[resourceIndex];
            uint256 price = offerResourceParams.resourcePrices[resourceId];
            IOfferV2.ResourceDescription storage resourceData = offerV2Storage.resources[resourceId];

            offerPriceViews[resourceIndex] = IOfferV2.OfferV2PriceView({
                resourceId: resourceId,
                ty: resourceData.ty,
                metadata: resourceData.metadata,
                price: price
            });
        }

        return offerPriceViews;
    }

    function getPeers(bytes32 offerId) internal view returns(IOfferV2.PeerV2View[] memory) {
        OfferStorageV2 storage offerV2Storage = storeV2();
        bytes32[] memory peerIds = offerV2Storage.peers[offerId].values();
        IOfferV2.PeerV2View[] memory peerViews = new IOfferV2.PeerV2View[](peerIds.length);

        for (uint256 peerIndex = 0; peerIndex < peerIds.length; peerIndex++) {
            bytes32 peerId = peerIds[peerIndex];
            peerViews[peerIndex] = getPeer(peerId);
        }

        return peerViews;
    }

    function getPeer(bytes32 peerId) internal view returns(IOfferV2.PeerV2View memory) {
        IBaseOffer.ComputePeer memory peer = LibBaseOffer.getComputePeer(peerId);

        return IOfferV2.PeerV2View({
            offerId: peer.offerId,
            commitmentId: peer.commitmentId,
            peerId: peerId,
            unitIds: LibBaseOffer.getComputeUnits(peerId),
            resources: getPeerResourcesFull(peerId)
        });
    }

    function getPeerResourcesFull(bytes32 peerId) internal view returns(IOfferV2.PeerV2ResourceView[] memory) {
        OfferStorageV2 storage offerV2Storage = storeV2();
        IOfferV2.PeerResourceParams storage peerResources = offerV2Storage.peerResourcesById[peerId];
        bytes32[] memory resourceIds = peerResources.resourceIds.values();
        IOfferV2.PeerV2ResourceView[] memory resourceViews = new IOfferV2.PeerV2ResourceView[](resourceIds.length);

        for (uint256 resourceIndex; resourceIndex < resourceIds.length; resourceIndex++) {
            bytes32 resourceId = resourceIds[resourceIndex];
            IOfferV2.PeerResource storage peerResource = peerResources.resources[resourceId];
            IOfferV2.ResourceDescription storage resourceData = offerV2Storage.resources[resourceId];

            resourceViews[resourceIndex] = IOfferV2.PeerV2ResourceView({
                resourceId: resourceId,
                ty: resourceData.ty,
                metadata: resourceData.metadata,
                details: peerResource.details,
                availableSupply: peerResource.availableSupply,
                maxSupply: peerResource.maxSupply
            });
        }

        return resourceViews;
    }

    function getDatacenter(bytes32 id) internal view returns(IOfferV2.Datacenter memory) {
        require(storeV2().datacenterIds.contains(id), "LibOfferV2: datacenter does not exist");
        return storeV2().datacenters[id];
    }

    function getDatacenters() internal view returns(IOfferV2.Datacenter[] memory) {
        uint256 datacentersLength = storeV2().datacenterIds.length();
        IOfferV2.Datacenter[] memory datacenters = new IOfferV2.Datacenter[](datacentersLength);

        for (uint256 datacenterIndex = 0; datacenterIndex < datacentersLength; datacenterIndex++) {
            bytes32 id = storeV2().datacenterIds.at(datacenterIndex);
            datacenters[datacenterIndex] = storeV2().datacenters[id];
        }

        return datacenters;
    }
}

