pragma solidity ^0.8.0;

import {PRECISION} from "src/utils/Common.sol";

struct OraclePriceStorage {
    uint256 firstTimestamp;
    uint256 priceInterval;
    uint256[] pricesCumulative; // A/B units
    uint256[] revPricesCumulative; // B/A units
    uint256 lastStoredPrice; // A/B units
}

library LibOraclePrice {
    bytes32 private constant _STORAGE_SLOT = bytes32(uint256(keccak256("fluence.oracle.price.storage.v1")) - 1);

    function store() internal pure returns (OraclePriceStorage storage s) {
        bytes32 storageSlot = _STORAGE_SLOT;
        assembly {
            s.slot := storageSlot
        }
    }

    function normalizeTimestamp(uint256 timestamp) internal view returns (uint256) {
        OraclePriceStorage storage s = store();
        return timestamp / s.priceInterval * s.priceInterval;
    }

    // price in A/B units
    function init(uint256 initialPrice, uint256 _priceInterval) internal { // shall be executed at diamond constructor
        OraclePriceStorage storage s = store();
        s.priceInterval = _priceInterval;
        s.firstTimestamp = normalizeTimestamp(block.timestamp);
        s.lastStoredPrice = initialPrice;
        s.pricesCumulative.push(initialPrice);
        s.revPricesCumulative.push(revPrice(initialPrice));
    }

    function update(uint256 newPrice) internal {
        OraclePriceStorage storage s = store();
        uint256 pricesLength = s.pricesCumulative.length;
        uint256 lastStoredPrice = s.lastStoredPrice;
        uint256 lastStoredRevPrice = revPrice(s.lastStoredPrice);
        uint256 newRevPrice = revPrice(newPrice);
        assert(pricesLength > 0); // should have been set in init()
        uint256 lastStoredTimestamp = s.firstTimestamp + s.priceInterval * (pricesLength - 1);
        uint256 currentTimestamp = normalizeTimestamp(block.timestamp);
        uint256 addedIntervals = (currentTimestamp - lastStoredTimestamp) / s.priceInterval;
        if (addedIntervals == 0) {
            s.pricesCumulative[pricesLength - 1] = s.pricesCumulative[pricesLength - 1] + newPrice - lastStoredPrice;
            s.revPricesCumulative[pricesLength - 1] = s.revPricesCumulative[pricesLength - 1] + newRevPrice - lastStoredRevPrice;
            s.lastStoredPrice = newPrice;
            return;
        }
        if (addedIntervals > 1) {
            uint256 lastCumulativePrice = s.pricesCumulative[pricesLength - 1];
            uint256 lastCumulativeRevPrice = s.revPricesCumulative[pricesLength - 1];
            for (uint256 i = 0; i < addedIntervals - 1; i++) {
                s.pricesCumulative.push(lastCumulativePrice + lastStoredPrice * (i + 1));
                s.revPricesCumulative.push(lastCumulativeRevPrice + lastStoredRevPrice * (i + 1));
            }
        }
        s.lastStoredPrice = newPrice;
        s.pricesCumulative.push(s.pricesCumulative[s.pricesCumulative.length - 1] + newPrice);
        s.revPricesCumulative.push(s.revPricesCumulative[s.revPricesCumulative.length - 1] + newRevPrice);

    }

    /**
     * @dev Private function to get index and price at timestamp
     * @param timestamp Timestamp to get index and price for
     */
    function getIndexAndRevPriceAtTimestamp(uint256 timestamp) private view returns (uint256 index, uint256 price) {
        OraclePriceStorage storage s = store();

        index = (timestamp - s.firstTimestamp) / s.priceInterval;

        if (index < s.pricesCumulative.length) { // same length for revPricesCumulative and pricesCumulative
            price = s.revPricesCumulative[index];
        } else {
            // does this trigger at all?
            uint256 intervalsMissed = (timestamp - s.firstTimestamp) / s.priceInterval - s.pricesCumulative.length + 1;
            uint256 lastStoredPrice = revPrice(s.lastStoredPrice);
            price = s.revPricesCumulative[s.revPricesCumulative.length - 1];
            price += lastStoredPrice * intervalsMissed;
        }
    }

    // mean for B/A units
    // (1/x_1 + ... + 1/x_n) / n
    function getMeanRevPriceBetweenTimestamps(uint256 fromTimestamp, uint256 toTimestamp) internal view returns (uint256) {
        OraclePriceStorage storage s = store();
        require(toTimestamp < normalizeTimestamp(block.timestamp), "Mean price can change in suggested interval");
        require(s.firstTimestamp <= fromTimestamp, "LibOraclePrice.getMeanRevPriceBetweenTimestamps: fromTimestamp is lower than first recorded timestamp");
        require(fromTimestamp <= toTimestamp, "LibOraclePrice.getMeanRevPriceBetweenTimestamps: fromTimestamp is greater than toTimestamp");

        (uint256 fromIndex, uint256 leftCumulativePrice) = getIndexAndRevPriceAtTimestamp(fromTimestamp);
        (uint256 toIndex, uint256 rightCumulativePrice) = getIndexAndRevPriceAtTimestamp(toTimestamp);

        if (fromIndex == toIndex) {
            return getRevPriceAtTimestamp(fromTimestamp);
        } else {
            return (rightCumulativePrice - leftCumulativePrice) / (toIndex - fromIndex);
        }
    }

    function getPriceAtTimestamp(uint256 timestamp) internal view returns (uint256) {
        OraclePriceStorage storage s = store();
        require(s.firstTimestamp <= timestamp, "LibOraclePrice.getPriceAtTimestamp: timestamp is lower than first recorded timestamp");

        uint256 index = (timestamp - s.firstTimestamp) / s.priceInterval;

        if (index >= s.pricesCumulative.length) {
           return s.lastStoredPrice;
        }

        if (index == 0) {
            return s.pricesCumulative[0];
        }

        return s.pricesCumulative[index] - s.pricesCumulative[index - 1];
    }

    function getRevPriceAtTimestamp(uint256 timestamp) internal view returns (uint256) {
        OraclePriceStorage storage s = store();
        require(s.firstTimestamp <= timestamp, "LibOraclePrice.getRevPriceAtTimestamp: timestamp is lower than first recorded timestamp");

        uint256 index = (timestamp - s.firstTimestamp) / s.priceInterval;

        if (index >= s.revPricesCumulative.length) {
            return revPrice(s.lastStoredPrice);
        }

        if (index == 0) {
            return s.revPricesCumulative[0];
        }

        return s.revPricesCumulative[index] - s.revPricesCumulative[index - 1];
    }

    function lastSetPrice() internal view returns (uint256) {
        OraclePriceStorage storage s = store();
        return s.lastStoredPrice;
    }

    function lastSetRevPrice() internal view returns (uint256) {
        OraclePriceStorage storage s = store();
        return revPrice(s.lastStoredPrice);
    }

    function revPrice(uint256 price) internal pure returns (uint256) {
        return PRECISION * PRECISION / price;
    }

    function priceInterval() internal view returns (uint256) {
        return store().priceInterval;
    }

    function lastPriceSetting() internal view returns (uint256 timestamp, uint256 price) {
        OraclePriceStorage storage s = store();
        timestamp = s.firstTimestamp + s.priceInterval * (s.pricesCumulative.length - 1);
        price = s.lastStoredPrice;
    }
}

