pragma solidity ^0.8.0;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

struct SendValueStorage {
    address payable WFLT; // WETH9 FLT <-> WFLT wrapper contract
}

uint256 constant MAX_GAS_FOR_CALL = 34_300; // send value to a cold empty address

library LibSendValue {
    using SafeERC20 for IERC20;
    bytes32 private constant _STORAGE_SLOT = bytes32(uint256(keccak256("fluence.send_value.storage.v1")) - 1);

    function store() internal pure returns (SendValueStorage storage s) {
        bytes32 storageSlot = _STORAGE_SLOT;
        assembly {
            s.slot := storageSlot
        }
    }

    function sendNativeOrWrapped(address payable recipient, uint256 amount) internal {
        SendValueStorage storage s = store();
        (bool success, ) = recipient.call{value: amount, gas: MAX_GAS_FOR_CALL}("");
        if (!success) {
            (bool successWFLT, ) = s.WFLT.call{value: amount}("");
            require(successWFLT, "LibSendValue: failed wrap FLT to WFLT");
            IERC20(s.WFLT).safeTransfer(recipient, amount);
        }
    }
}

