pragma solidity ^0.8.19;

import {ArbSys} from "src/interfaces/ArbSysInterface.sol";


/// @custom:fluence-abi-checker
abstract contract ArbHelper {
    address constant ARB_SYS = address(100); // precompiled address for arbitrum chains

    // DON'T CREATE STORAGE SLOTS HERE! (constant vars don't occupy ones)
    // as there are contracts that are inherited from it, it will break storage

    function getL2BlockNumber() internal returns (uint256) {
        if (ARB_SYS.code.length != 0) {
            return ArbSys(ARB_SYS).arbBlockNumber();
        } else {
            // in tests or not arbitrum
            return block.number;
        }
    }
}

