pragma solidity ^0.8.0;

import {ReentrancyGuardTransient} from "@openzeppelin/contracts/utils/ReentrancyGuardTransient.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC173} from "src/interfaces/IERC173.sol";
import {IBalanceKeeper} from "src/balance-keeper/IBalanceKeeper.sol";
import {IDealFactory} from "src/core/interfaces/IDealFactory.sol";
import {ArbHelper} from "src/ArbHelper.sol";


/// @custom:fluence-abi-checker
contract BalanceKeeper is UUPSUpgradeable, IBalanceKeeper, ReentrancyGuardTransient, ArbHelper {
    using SafeERC20 for IERC20;

    uint256 constant public WITHDRAW_REQUEST_DELAY = 7 days;

    address public diamond;
    address public coordinator;

    // free balance is an unallocated balance
    // deal balance is a balance allocated to deals
    mapping(IERC20 token => mapping(address user => uint256 balance)) private freeBalances;
    mapping(IERC20 token => mapping(address user => uint256 balance)) private dealBalances;
    mapping(IERC20 token => mapping(address user => uint256 timestamp)) private terminationRequests;
    mapping(IERC20 token => uint256 supply) public freeBalanceTotal;
    mapping(IERC20 token => uint256 supply) public dealBalancesTotal;

    mapping(IERC20 token => uint256 amount) public poolBalance;
    mapping(IERC20 token => mapping(address user => uint256 balance)) public limbo;
    mapping(IERC20 token => uint256 amount) public limboTotal;

    mapping(address user => bool frozen) public frozen;

    mapping(string => bool) public topUpIds;

    bool public manualWithdrawEnabled = false;

    DealDepositInfo[] private dealDeposits;

    function owner() private view returns (address) {
        return IERC173(diamond).owner();
    }

    modifier onlyDiamondOwner {
        require(msg.sender == owner(), "BalanceKeeper: Only owner can call this function");
        _;
    }

    modifier onlyDeal() {
        require(IDealFactory(diamond).hasDeal(msg.sender), "BalanceKeeper: Only deal can call this function");
        _;
    }

    modifier onlyVodopad() {
        require(msg.sender == IDealFactory(diamond).vodopad(), "BalanceKeeper: Only vodopad can call this function");
        _;
    }

    modifier onlyCoordinator() {
        require(msg.sender == coordinator, "BalanceKeeper: Only coordinator can call this function");
        _;
    }

    modifier onlyCoordinatorOrDiamondOwner() {
        require(
            msg.sender == coordinator || msg.sender == owner(),
            "BalanceKeeper: Only coordinator or diamond owner can call this function"
        );
        _;
    }

    modifier onlyIfNotFrozen(address user) {
        require(!frozen[user], "BalanceKeeper: User is permanently frozen");
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function increaseFreeBalance(
        IERC20 token,
        address user,
        uint256 amount,
        BalanceChangeReason reason,
        address info
    ) private {
        freeBalances[token][user] += amount;
        freeBalanceTotal[token] += amount;
        emit FreeBalanceIncrease(token, user, reason, amount, info);
    }

    function decreaseFreeBalance(
        IERC20 token,
        address user,
        uint256 amount,
        BalanceChangeReason reason,
        address info
    ) private {
        freeBalances[token][user] -= amount;
        freeBalanceTotal[token] -= amount;
        emit FreeBalanceDecrease(token, user, reason, amount, info);
    }

    function initialize(
        address _diamond
    ) external initializer {
        diamond = _diamond;
        __UUPSUpgradeable_init();
    }

    function getFreeBalance(IERC20 token, address user) external view returns (uint256) {
        return freeBalances[token][user];
    }

    function getFullBalance(IERC20 token, address user) external view returns (uint256) {
        return freeBalances[token][user] + dealBalances[token][user];
    }

    /*
     * Sends the specified amount of tokens to limbo, freezing them for user
     */
    function _sendToLimbo(IERC20 token, address user) private {
        uint256 amount = freeBalances[token][user];
        require(amount > 0, "BalanceKeeper: _sendToLimbo: not enough balance");
        require(freeBalanceTotal[token] >= amount, "BalanceKeeper: _sendToLimbo: not enough total balance");
        limbo[token][user] += amount;
        limboTotal[token] += amount;
        decreaseFreeBalance(token, user, amount, BalanceChangeReason.SEND_TO_LIMBO, msg.sender);
        emit SentToLimbo(token, user, msg.sender, amount);
    }

    // METHODS FROM COORDINATOR
    function poolTopUp(IERC20 token, uint256 amount) external nonReentrant onlyCoordinatorOrDiamondOwner {
        poolBalance[token] += amount;
        token.safeTransferFrom(msg.sender, address(this), amount);
        emit PoolTopUp(token, msg.sender, amount);
    }

    function poolWithdraw(IERC20 token, uint256 amount) external nonReentrant onlyDiamondOwner {
        require(poolBalance[token] >= amount, "BalanceKeeper: not enough amount to withdraw from pool");
        poolBalance[token] -= amount;
        token.safeTransfer(msg.sender, amount);
        emit PoolWithdraw(token, msg.sender, amount);
    }

    function enableOnchainRefund() external onlyCoordinator {
        manualWithdrawEnabled = true;
        emit ManualWithdrawEnabled(msg.sender);
    }

    function disableOnchainRefund() external onlyCoordinator {
        manualWithdrawEnabled = false;
        emit ManualWithdrawDisabled(msg.sender);
    }

    /// @dev Refunds the specified amount of tokens to the user
    function refund(IERC20 token, address user, uint256 amount) external nonReentrant onlyCoordinator {
        require(freeBalanceTotal[token] >= amount, "BalanceKeeper: vodopadWithdraw: not enough total balance");
        require(freeBalances[token][user] >= amount, "BalanceKeeper: vodopadWithdraw: not enough balance");
        decreaseFreeBalance(token, user, amount, BalanceChangeReason.REFUND, msg.sender);
        token.safeTransfer(user, amount);
        emit Refunded(token, user, amount, msg.sender);
    }

    function _freezeAccount(address user, bool freeze) private {
        frozen[user] = freeze;
        emit AccountFreezeStateChanged(user, freeze, msg.sender);
    }

    function freezeAccount(address user) external onlyCoordinator {
        _freezeAccount(user, true);
    }

    function unfreezeAccount(address user) external onlyCoordinator {
        _freezeAccount(user, false);
    }

    function sendToLimbo(IERC20 token, address user) external onlyCoordinator {
        _sendToLimbo(token, user);
    }

    function terminationVeto(IERC20 token, address user) external onlyCoordinator {
        require(terminationRequests[token][user] > 0, "BalanceKeeper: veto: no request");
        terminationRequests[token][user] = 0;
        _sendToLimbo(token, user);
        _freezeAccount(user, true);
        emit TerminationVeto(token, user, msg.sender);
    }

    // METHODS FROM VODOPAD
    function userTopUp(IERC20 token, address user, uint256 amount, string memory topUpId) external onlyIfNotFrozen(user) onlyVodopad {
        require(topUpIds[topUpId] == false, "BalanceKeeper: userTopUp: top-up id already used");
        topUpIds[topUpId] = true;
        require(poolBalance[token] >= amount, "BalanceKeeper: userTopUp: not enough gateway balance");
        poolBalance[token] -= amount;
        increaseFreeBalance(token, user, amount, BalanceChangeReason.USER_TOP_UP, msg.sender);
        emit UserBalanceIncrease(token, user, msg.sender, amount, topUpId);
    }

    // METHODS FROM DEAL
    function moveBalanceFromDeal(IERC20 token, address user, uint256 amount, uint8 reserved)
        external onlyDeal onlyIfNotFrozen(user) nonReentrant
    {
        require(dealBalances[token][user] >= amount, "BalanceKeeper: moveBalanceFromDeal: not enough balance");
        require(dealBalancesTotal[token] >= amount, "BalanceKeeper: moveBalanceFromDeal: not enough total balance");
        dealBalances[token][user] -= amount;
        dealBalancesTotal[token] -= amount;
        increaseFreeBalance(token, user, amount, BalanceChangeReason.MOVE_FROM_DEAL, msg.sender);


        emit DealWithdraw(token, msg.sender, user, amount, reserved);
    }

    function moveFreeBalance(IERC20 token, address from, address to, uint256 amount, uint8 reserved)
        external onlyDeal onlyIfNotFrozen(from) onlyIfNotFrozen(to) nonReentrant
    {
        require(freeBalances[token][from] >= amount, "BalanceKeeper: moveFreeBalance: not enough balance");
        decreaseFreeBalance(token, from, amount, BalanceChangeReason.MOVE_FREE_BALANCE, to);
        increaseFreeBalance(token, to, amount, BalanceChangeReason.MOVE_FREE_BALANCE, from);

        emit BalanceMoved(token, from, to, amount, reserved);
    }

    // moves funds from free to deal balance
    function moveBalanceToDeal(IERC20 token, address user, uint256 amount, DealDepositReason reason)
        external onlyDeal onlyIfNotFrozen(user) nonReentrant
    {
        require(freeBalances[token][user] >= amount, "BalanceKeeper: moveBalanceToDeal: not enough balance");
        require(freeBalanceTotal[token] >= amount, "BalanceKeeper: moveBalanceToDeal: not enough total balance");
        dealBalances[token][user] += amount;
        dealBalancesTotal[token] += amount;
        decreaseFreeBalance(token, user, amount, BalanceChangeReason.MOVE_TO_DEAL, msg.sender);

        uint256 blockNumber = getL2BlockNumber();
        DealDepositInfo memory depositInfo = DealDepositInfo({
            token: token,
            deal: msg.sender,
            user: user,
            amount: amount,
            reason: reason,
            blockNumber: blockNumber,
            reserved1: 0,
            reserved2: 0,
            reserved3: 0
        });
        dealDeposits.push(depositInfo);

        emit DealDeposit(token, msg.sender, user, amount, reason, blockNumber, dealDeposits.length - 1);
    }

    function getDepositInfoLength() external view returns (uint256) {
        return dealDeposits.length;
    }

    function getDepositInfo(uint256 from, uint256 to) external view returns (DealDepositInfo[] memory) {
        require(to >= from, "storedDeals: to < from");
        require(to < dealDeposits.length, "getDepositInfo: overflow");
        DealDepositInfo[] memory result = new DealDepositInfo[](to - from + 1);
        for (uint index = from; index <= to; ++index) {
            result[index - from] = dealDeposits[index];
        }
        return result;
    }

    function withdrawByDeal(IERC20 token, address user, uint256 amount, uint8 reserved) external onlyDeal onlyIfNotFrozen(user) nonReentrant {
        require(freeBalances[token][user] >= amount, "BalanceKeeper: withdrawByDeal: not enough balance");
        require(freeBalanceTotal[token] >= amount, "BalanceKeeper: withdrawByDeal: not enough total balance");
        decreaseFreeBalance(token, user, amount, BalanceChangeReason.WITHDRAW_BY_DEAL, msg.sender);
        token.safeTransfer(user, amount);
        emit Withdrawn(token, user, amount, reserved, msg.sender);
    }

    // METHODS FROM USER
    function terminationRequest(IERC20 token) external onlyIfNotFrozen(msg.sender) {
        require(dealBalances[token][msg.sender] == 0, "BalanceKeeper: vodopadWithdraw: deal balance is not empty");
        require(manualWithdrawEnabled, "BalanceKeeper: withdrawRequest: manual withdraw disabled");
        require(freeBalances[token][msg.sender] > 0, "BalanceKeeper: withdrawRequest: not enough balance");
        require(terminationRequests[token][msg.sender] == 0, "BalanceKeeper: withdrawRequest: already requested");
        terminationRequests[token][msg.sender] = block.timestamp;
        emit TerminationRequestCreated(token, msg.sender, msg.sender);
    }

    function terminate(IERC20 token) external onlyIfNotFrozen(msg.sender) nonReentrant {
        require(manualWithdrawEnabled, "BalanceKeeper: withdraw: manual withdraw disabled");
        require(terminationRequests[token][msg.sender] > 0, "BalanceKeeper: withdraw: no request");
        require(block.timestamp - terminationRequests[token][msg.sender] >= WITHDRAW_REQUEST_DELAY, "BalanceKeeper: withdraw: too early");
        require(freeBalances[token][msg.sender] > 0, "BalanceKeeper: withdraw: not enough balance");
        uint256 amount = freeBalances[token][msg.sender];
        decreaseFreeBalance(token, msg.sender, amount, BalanceChangeReason.TERMINATE, address(0));
        terminationRequests[token][msg.sender] = 0;
        token.safeTransfer(msg.sender, amount);
        emit TerminationExecuted(token, msg.sender, msg.sender);
    }

    // METHODS FROM DIAMOND OWNER
    function setCoordinator(address _coordinator) external onlyDiamondOwner {
        coordinator = _coordinator;
        emit CoordinatorSet(_coordinator, msg.sender);
    }

    function withdrawFromLimbo(IERC20 token, address user, address to, uint256 amount) external nonReentrant onlyDiamondOwner {
        require(limbo[token][user] >= amount, "BalanceKeeper: withdrawFromLimbo: not enough balance");
        limbo[token][user] -= amount;
        limboTotal[token] -= amount;
        token.safeTransfer(to, amount);
        emit WithdrawnFromLimbo(token, user, to, msg.sender, amount);
    }

    function fundFromLimbo(IERC20 token, address user, uint256 amount) external onlyDiamondOwner {
        require(limbo[token][user] >= amount, "BalanceKeeper: fundFromLimbo: not enough balance");
        limbo[token][user] -= amount;
        limboTotal[token] -= amount;
        increaseFreeBalance(token, user, amount, BalanceChangeReason.FUND_FROM_LIMBO, msg.sender);
        emit FundedFromLimbo(token, user, msg.sender, amount);
    }

    function _authorizeUpgrade(address) internal override onlyDiamondOwner {
    }
}

