/// @title Offer contract interface
/// @dev Offer contract is responsible for managing the offers in the market
interface IOfferV2 {
    enum ResourceType {
        VCPU,
        RAM,
        STORAGE,
        PUBLIC_IP,
        NETWORK_BANDWIDTH,
        GPU
    }

    struct Datacenter {
        bytes32 id;
        string countryCode;
        string cityCode;
        uint256 index;
        uint256 tier;
        string[] certifications;
    }

    // Unique across chain, not just per resource type
    struct ResourceDescription {
        ResourceType ty;
        string metadata;
    }

    // Unique across chain, not just per resource type
    struct ResourceDescriptionView {
        bytes32 id;
        ResourceType ty;
        string metadata;
    }

    struct OfferResourcesParams {
        mapping(bytes32 resourceId => uint256 price) resourcePrices;
        EnumerableSet.Bytes32Set resourceIds;
    }

    struct PeerResourceParams {
        mapping(bytes32 resourceId => PeerResource) resources;
        EnumerableSet.Bytes32Set resourceIds;
    }

    struct PeerResource {
        uint256 availableSupply;
        uint256 maxSupply;
        string details;
    }

    struct ResourceRequirement {
        bytes32 resourceId;
        IOfferV2.ResourceType ty;
        uint256 quantity;
    }

    struct RegisterResourcePrice {
        ResourceType ty;
        bytes32 resourceId;
        uint256 price;
    }

    struct RegisterPeerResource {
        bytes32 resourceId;
        uint256 supply;
        string details;
    }

    struct RegisterComputePeerV2 {
        bytes32 peerId;
        address owner;
        RegisterPeerResource[] resources;
        bytes32[] unitIds;
    }

    struct UpgradeComputePeerV2 {
        bytes32 peerId;
        RegisterPeerResource[] resources;
    }

    struct RegisterDatacenter {
        string countryCode;
        string cityCode;
        uint256 index;
        uint256 tier;
        string[] certifications;
    }

    struct OfferV2PriceView {
        bytes32 resourceId;
        ResourceType ty;
        string metadata;
        uint256 price;
    }

    struct PeerV2ResourceView {
        bytes32 resourceId;
        ResourceType ty;
        string metadata;
        string details;
        uint256 availableSupply;
        uint256 maxSupply;
    }

    struct OfferV2View {
        bytes32 offerId;
        address provider;
        address paymentToken;
        //bytes32 datacenterId; //todo unlock when breaking api
        OfferV2PriceView[] prices;
    }

    struct PeerV2View {
        bytes32 offerId;
        bytes32 peerId;
        bytes32 commitmentId;
        IBaseOffer.ComputeUnitView[] unitIds;
        PeerV2ResourceView[] resources;
    }

    event OfferV2Created(
        bytes32 indexed offerId,
        address paymentToken,
        address provider,
        bytes32 datacenterId
    );

    event PeerResourceRegistered(bytes32 indexed peerId, bytes32 resourceId, uint256 maxSupply, string details);

    event PeerResourceRemoved(bytes32 indexed peerId, bytes32 resourceId);

    event PeerResourceSupplyUpdated(bytes32 indexed peerId, bytes32 resourceId, uint256 newAvailableSupply, uint256 newMaxSupply);

    event PeerResourceDetailsUpdated(bytes32 indexed peerId, bytes32 resourceId, string details);

    event ResourceCreated(bytes32 indexed resourceId, ResourceType ty, string metadata);

    event ResourceMetadataUpdated(bytes32 indexed resourceId, string metadata);

    event ResourcePriceUpdated(bytes32 indexed offerId, bytes32 resourceId, uint256 price);

    event OfferDatacenterUpdated(bytes32 indexed offerId, bytes32 datacenterId);

    event DatacenterCreated(bytes32 indexed id, string countryCode, string cityCode, uint256 index, uint256 tier, string[] certifications);

    event DatacenterUpdated(bytes32 indexed id, uint256 tier, string[] certifications);

    function registerMarketOfferV2(
        address paymentToken,
        RegisterResourcePrice[] calldata resourcePrices,
        RegisterComputePeerV2[] calldata peers,
        bytes32 datacenterId
    ) external returns (bytes32);

    function upgradeMarketOfferV2(
        bytes32 offerId,
        RegisterResourcePrice[] calldata resourcePrices,
        UpgradeComputePeerV2[] calldata peers,
        bytes32 datacenterId
    ) external;

    function addComputePeersV2(bytes32 offerId, RegisterComputePeerV2[] calldata peers) external;

    function addComputeUnitsV2(bytes32 peerId, bytes32[] calldata unitIds, IOfferV2.RegisterPeerResource calldata resource) external;

    function removeOfferV2(bytes32 offerId) external;

    function removeComputePeerV2(bytes32 peerId) external;

    function removeComputeUnitV2(bytes32 unitId, bytes32 associatedResourceId) external;

    function changeResourcePriceV2(bytes32 offerId, bytes32 resourceId, uint256 newPrice) external;

    function changeResourceMaxSupplyV2(bytes32 peerId, bytes32 resourceId, uint256 newMaxSupply) external;

    function changeResourceDetails(bytes32 peerId, bytes32 resourceId, string calldata details) external;

    function removePeerResource(bytes32 peerId, bytes32 resourceId) external;

    function registerPeerResource(bytes32 peerId, RegisterPeerResource calldata resource) external;

    function registerResources(ResourceDescription[] calldata resources) external returns(bytes32[] memory);

    function updateResourceMetadata(bytes32 resourceId, string calldata metadata) external;

    function ramPerVcpu() external pure returns(uint256);

    function vcpuPerCu() external pure returns(uint256);

    function bytesPerRam() external pure returns(uint256);

    function bytesPerStorage() external pure returns(uint256);

    function createDatacenter(RegisterDatacenter calldata datacenter) external returns(bytes32);

    function createDatacenters(RegisterDatacenter[] calldata datacenters) external returns(bytes32[] memory);

    function updateDatacenter(bytes32 id, uint256 tier, string[] calldata certifications) external;

    function setOfferDatacenter(bytes32 offerId, bytes32 datacenterId) external;

    function getOfferDatacenter(bytes32 offerId) external view returns(bytes32);
}

