interface IWorkerManagerV2 is IConfigV2 {
    // ------------------ Types ------------------
    struct WorkerDataV2 {
        bytes32 onchainId;
    }

    struct Worker {
        bytes32 offchainId;
        bytes32 onchainId;
        bytes32 peerId;
        address provider;
        uint256 joinedEpoch;
    }

    // ------------------ Events ------------------

    /// @dev Emitted when a new worker is added
    /// @param peerId The id of compute peer
    /// @param onchainId The worker ID
    event WorkerJoined(bytes32 indexed peerId, bytes32 onchainId);

    /// @dev Emitted when a worker is removed
    /// @param peerId The id of compute peer
    /// @param onchainId The worker onchain ID
    event WorkerRemoved(bytes32 indexed peerId, bytes32 onchainId);

    /// @dev Emitted when a worker ID is updated
    /// @param onchainWorkerId The worker
    /// @param offchainWorkerId The new worker ID
    event WorkerIdUpdated(bytes32 onchainWorkerId, bytes32 offchainWorkerId);

    /// @dev Returns total number of workers
    function getWorkerCount() external view returns (uint256);

    /// @dev Returns number of workers in provider
    function getWorkerCount(address provider) external view returns (uint256);

    /// @dev Returns workers registered in this deal
    function getWorkerIds() external view returns (bytes32[] memory);

    /// @dev Returns the worker count. Worker is a compute unit that has a worker ID
    function getActivatedWorkerCount() external view returns (uint256);

    /// @dev Returns the boolean flag indicating whether the peer exists in the deal
    function computePeerUsed(bytes32 peerId) external view returns (bool);
}

