 * along with this program.  If not, see <https://www.gnu.org/licenses/>.
 */

pragma solidity ^0.8.0;

import {IBaseOffer} from "src/core/interfaces/IBaseOffer.sol";
import {IOffer} from "src/core/interfaces/IOffer.sol";
import {LibCapacity} from "src/lib/LibCapacity.sol";
import {LibGlobalConst} from "src/lib/LibGlobalConst.sol";
import {IDeal} from "src/deal/v1/interfaces/IDeal.sol";
import {IWorkerManager} from "src/deal/v1/interfaces/IWorkerManager.sol";
import {CIDV1} from "src/utils/Common.sol";
import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import {OwnableUpgradableDiamond} from "src/utils/OwnableUpgradableDiamond.sol";

struct Effectors {
    mapping(bytes32 => bool) hasEffector;
}

struct OfferStorage {
    mapping(address => IBaseOffer.ProviderInfo) providers;               // BaseOffer
    mapping(bytes32 => IOffer.Offer) offers;                             // OfferV1 + BaseOffer (see struct)
    mapping(bytes32 => IBaseOffer.ComputePeer) peers;                    // LibBaseOffer
    mapping(bytes32 => IBaseOffer.ComputeUnit) computeUnits;             // LibBaseOffer
    mapping(bytes32 => Effectors) effectorsByOfferId;                    // OfferV1
    mapping(bytes32 => EnumerableSet.Bytes32Set) computeUnitIdsByPeerId; // LibBaseOffer
    mapping(bytes32 => IOffer.EffectorInfo) effectorInfoById;            // OfferV1
}

library LibBaseOffer {
    using EnumerableSet for EnumerableSet.Bytes32Set;
    bytes32 internal constant _OFFER_ID_PREFIX = bytes32(uint256(keccak256("fluence.market.offer")) - 1);
    bytes32 private constant _STORAGE_SLOT = bytes32(uint256(keccak256("fluence.market.storage.v1")) - 1);
    uint256 public constant PROTOCOL_V1 = 1;
    uint256 public constant PROTOCOL_V2 = 2;

    function store() internal pure returns (OfferStorage storage s) {
        bytes32 storageSlot = _STORAGE_SLOT;
        assembly {
            s.slot := storageSlot
        }
    }

    function getProviderInfo(address provider) internal view returns (IBaseOffer.ProviderInfo memory) {
        return store().providers[provider];
    }

    function getBaseOffer(bytes32 offerId) internal view returns (IBaseOffer.BaseOffer memory) {
        IOffer.Offer storage offer = store().offers[offerId];
        require(offer.provider != address(0x00), "Offer: offer doesn't exist");

        return IBaseOffer.BaseOffer({
            provider: offer.provider,
            paymentToken: offer.paymentToken,
            peerCount: offer.peerCount,
            minProtocolVersion: offer.minProtocolVersion,
            maxProtocolVersion: offer.maxProtocolVersion
        });
    }

    function getComputePeer(bytes32 peerId) internal view returns (IBaseOffer.ComputePeer memory) {
        IBaseOffer.ComputePeer storage computePeer = store().peers[peerId];
        require(computePeer.offerId != bytes32(0x00), "Peer doesn't exist");

        return store().peers[peerId];
    }

    function getComputeUnit(bytes32 unitId) internal view returns (IBaseOffer.ComputeUnit memory) {
        IBaseOffer.ComputeUnit storage computeUnit = store().computeUnits[unitId];
        require(computeUnit.peerId != bytes32(0x00), "getComputeUnit: Compute unit doesn't exist");

        return store().computeUnits[unitId];
    }

    function getComputeUnits(bytes32 peerId) internal view returns (IBaseOffer.ComputeUnitView[] memory) {
        OfferStorage storage offerStorage = store();

        bytes32[] memory unitIds = offerStorage.computeUnitIdsByPeerId[peerId].values();
        IBaseOffer.ComputeUnitView[] memory units = new IBaseOffer.ComputeUnitView[](unitIds.length);

        IBaseOffer.ComputePeer storage computePeer = offerStorage.peers[peerId];
        bytes32 commitmentId = computePeer.commitmentId;

        uint256 capacityStartEpoch;
        if (commitmentId != bytes32(0x00)) {
            capacityStartEpoch = LibCapacity.getCommitment(commitmentId).startEpoch;
        }

        for (uint256 i = 0; i < unitIds.length; i++) {
            bytes32 unitId = unitIds[i];
            IBaseOffer.ComputeUnit storage computeUnit = offerStorage.computeUnits[unitId];
            uint256 startEpoch = computeUnit.startEpoch;

            if (startEpoch == 0) {
                startEpoch = capacityStartEpoch;
            }

            units[i] = IBaseOffer.ComputeUnitView({id: unitId, deal: computeUnit.deal, startEpoch: startEpoch, onchainWorkerId: computeUnit.onchainWorkerId});
        }

        return units;
    }

    function getComputeUnitIds(bytes32 peerId) internal view returns (bytes32[] memory) {
        return store().computeUnitIdsByPeerId[peerId].values();
    }

    function setCommitmentId(bytes32 peerId, bytes32 commitmentId) internal {
        OfferStorage storage offerStorage = store();
        IBaseOffer.ComputePeer storage computePeer = offerStorage.peers[peerId];

        computePeer.commitmentId = commitmentId;
    }

    function setStartEpoch(bytes32 unitId, uint256 startEpoch) internal {
        OfferStorage storage offerStorage = store();
        IBaseOffer.ComputeUnit storage computeUnit = offerStorage.computeUnits[unitId];

        computeUnit.startEpoch = startEpoch;
    }

    function setProviderInfo(string calldata name, CIDV1 calldata metadata) internal {
        require(bytes(name).length > 0, "Name should be not empty");
        IBaseOffer.ProviderInfo storage provider = store().providers[msg.sender];
        provider.name = name;
        provider.metadata = metadata;

        emit IBaseOffer.ProviderInfoUpdated(msg.sender, name, metadata);
    }

    function registerBaseOffer(
        address paymentToken,
        uint256 minProtocolVersion,
        uint256 maxProtocolVersion
    ) internal returns (bytes32) {
        OfferStorage storage offerStorage = LibBaseOffer.store();

        address provider = msg.sender;
        bytes32 offerId = keccak256(abi.encodePacked(_OFFER_ID_PREFIX, provider, block.number, msg.data));

        require(bytes(offerStorage.providers[provider].name).length > 0, "Provider doesn't exist");
        require(offerStorage.offers[offerId].paymentToken == address(0x00), "Offer already exists");
        require(address(paymentToken) != address(0x00), "Payment token should be not zero address");
        require(minProtocolVersion >= LibGlobalConst.minProtocolVersion(), "Min protocol version too small");
        require(maxProtocolVersion <= LibGlobalConst.maxProtocolVersion(), "Max protocol version too big");
        require(minProtocolVersion <= maxProtocolVersion, "Wrong protocol versions");
        require(LibGlobalConst.isPaymentTokenAllowed(paymentToken), "Payment token is not allowed");

        // create market offer
        offerStorage.offers[offerId] = IOffer.Offer({
            provider: provider,
            minPricePerCuPerEpoch: 0,
            paymentToken: paymentToken,
            peerCount: 0,
            minProtocolVersion: minProtocolVersion,
            maxProtocolVersion: maxProtocolVersion
        });

        emit IBaseOffer.MarketOfferRegistered(
            provider, offerId, 0, paymentToken, new CIDV1[](0), minProtocolVersion, maxProtocolVersion
        );

        return offerId;
    }

    function removeBaseOffer(bytes32 offerId) internal {
        OfferStorage storage offerStorage = store();
        IOffer.Offer storage offer = offerStorage.offers[offerId];

        require(offer.provider != address(0), "Offer: offer doesn't exist");
        require(offer.provider == msg.sender, "Offer: only provider can call this method");
        require(offer.peerCount == 0, "Offer has compute peers");

        delete offerStorage.offers[offerId];

        emit IBaseOffer.MarketOfferRemoved(offerId);
    }

    function registerBaseComputePeer(
        bytes32 offerId,
        bytes32 peerId,
        address owner,
        bytes32[] calldata unitIds
    ) internal {
        OfferStorage storage offerStorage = store();

        IBaseOffer.ComputePeer storage computePeer = offerStorage.peers[peerId];
        IOffer.Offer storage offer = offerStorage.offers[offerId];

        require(computePeer.offerId == bytes32(0x00), "LibBaseOffer: Peer already exists in another offer");
        require(peerId != bytes32(0x00), "LibBaseOffer: Peer id should be not empty");
        require(owner != address(0x00), "LibBaseOffer: Owner address should be not empty");

        computePeer.offerId = offerId;
        computePeer.owner = owner;

        offer.peerCount++;

        emit IBaseOffer.PeerCreated(offerId, peerId, computePeer.owner);
        registerBaseComputeUnits(peerId, unitIds);
    }

    function removeBaseComputePeer(bytes32 peerId) internal {
        OfferStorage storage offerStorage = LibBaseOffer.store();
        IBaseOffer.ComputePeer storage computePeer = offerStorage.peers[peerId];
        bytes32 offerId = computePeer.offerId;
        IOffer.Offer storage offer = offerStorage.offers[offerId];

        require(offerId != bytes32(0x00), "Peer doesn't exist");
        require(offer.provider == msg.sender, "Offer: only provider can call this method");

        require(computePeer.unitCount == 0, "Peer has compute units");

        require(offer.peerCount > 0, "LibBaseOffer: attempt to remove a peer from an offer without peers");
        offer.peerCount--;
        delete offerStorage.peers[peerId];

        emit IBaseOffer.PeerRemoved(offerId, peerId);
    }

    function registerBaseComputeUnits(
        bytes32 peerId,
        bytes32[] calldata unitIds
    ) internal {
        OfferStorage storage offerStorage = store();
        IBaseOffer.ComputePeer storage computePeer = offerStorage.peers[peerId];
        IOffer.Offer storage offer = offerStorage.offers[computePeer.offerId];
        IBaseOffer.ProviderInfo storage provider = offerStorage.providers[offer.provider];

        uint256 unitCount = unitIds.length;

        require(unitCount > 0, "LibBaseOffer: units length should be greater than 0");
        require(provider.unitCount + unitCount <= LibGlobalConst.maxUnitCountPerProvider(), "LibBaseOffer: Exceeded max CU number per Provider");

        for (uint256 i = 0; i < unitCount; i++) {
            bytes32 unitId = unitIds[i];

            require(unitId != bytes32(0x00), "LibBaseOffer: Unit id should be not empty");
            require(offerStorage.computeUnits[unitId].peerId == bytes32(0x00), "LibBaseOffer: Compute unit already exists");

            // create compute unit
            offerStorage.computeUnits[unitId] = IBaseOffer.ComputeUnit({deal: address(0x00), peerId: peerId, startEpoch: 0, onchainWorkerId: 0});
            require(offerStorage.computeUnitIdsByPeerId[peerId].add(unitId), "LibBaseOffer: Invalid add to enumerable set");

            emit IBaseOffer.ComputeUnitCreated(peerId, unitId);
        }

        provider.unitCount += unitCount;
        computePeer.unitCount += unitCount;
    }

    function removeBaseComputeUnit(bytes32 unitId) internal {
        OfferStorage storage offerStorage = store();
        IBaseOffer.ComputeUnit storage computeUnit = offerStorage.computeUnits[unitId];

        bytes32 peerId = computeUnit.peerId;
        require(peerId != bytes32(0x00), "removeBaseComputeUnit: Compute unit doesn't exist");

        IBaseOffer.ComputePeer storage computePeer = offerStorage.peers[peerId];
        IOffer.Offer storage offer = offerStorage.offers[computePeer.offerId];
        IBaseOffer.ProviderInfo storage provider = offerStorage.providers[offer.provider];

        require(offer.provider == msg.sender, "Offer: protocol version mismatch, required v1");
        require(computePeer.commitmentId == bytes32(0x00), "Peer has commitment");
        require(computeUnit.deal == address(0x00), "Compute unit is in deal");

        require(computePeer.unitCount > 0, "LibBaseOffer: cannot remove compute unit because peer has zero units");
        require(provider.unitCount > 0, "LibBaseOffer: cannot remove compute unit because provider has zero units");
        computePeer.unitCount--;
        provider.unitCount--;

        require(offerStorage.computeUnitIdsByPeerId[peerId].remove(unitId), "Invalid remove from enumerable set");

        delete offerStorage.computeUnits[unitId];

        emit IBaseOffer.ComputeUnitRemoved(peerId, unitId);
    }

    function changePaymentToken(bytes32 offerId, address newPaymentToken) internal {
        OfferStorage storage offerStorage = store();
        IOffer.Offer storage offer = offerStorage.offers[offerId];

        require(offer.provider == msg.sender, "Offer: only provider can call this method");
        require(newPaymentToken != address(0x00), "Payment token should be not zero address");
        require(LibGlobalConst.isPaymentTokenAllowed(newPaymentToken), "Payment token is not allowed");

        offer.paymentToken = newPaymentToken;

        emit IBaseOffer.PaymentTokenUpdated(offerId, newPaymentToken);
    }

    function upgradeProtocolVersion(bytes32 offerId, uint256 minProtocolVersion, uint256 maxProtocolVersion) internal {
        IOffer.Offer storage offer = store().offers[offerId];
        require(offer.provider != address (0), "LibBaseOffer: offer does not exist");
        require(minProtocolVersion <= maxProtocolVersion, "LibBaseOffer: min protocol version is larger than max protocol version");
        require(offer.minProtocolVersion <= minProtocolVersion, "LibBaseOffer: attempt to downgrade minProtocolVersion during version upgrade");
        require(offer.maxProtocolVersion <= maxProtocolVersion, "LibBaseOffer: attempt to downgrade manProtocolVersion during version upgrade");

        offer.minProtocolVersion = minProtocolVersion;
        offer.maxProtocolVersion = maxProtocolVersion;

        emit IBaseOffer.ProtocolVersionUpgraded(offerId, minProtocolVersion, maxProtocolVersion);
    }
}

